import React, { useEffect, useState } from "react";
import api from "../axiosSetup.js";
import Header from "../components/Header.js";
import { Line, Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// регистрация модулей Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Reports = () => {
  const [summary, setSummary] = useState({});
  const [sales, setSales] = useState([]);
  const [categories, setCategories] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([
      api.get("/reports/orders-view"),
      api.get("/reports/books-view"),
      api.get("/reports/top-books-view"),
      api.get("/reports/orderdetails-view"),
      api.get("/reports/usersaccounts-view"),
    ])
      .then(([ordersRes, booksRes, topBooksRes, orderDetailsRes, usersRes]) => {
        const orders = ordersRes.data || [];
        const books = booksRes.data || [];
        const topBooks = topBooksRes.data || [];
        const orderDetails = orderDetailsRes.data || [];
        const usersData = usersRes.data || [];

        // === Сводные данные ===
        const totalOrders = orders.length;
        const totalRevenue = orderDetails.reduce(
          (acc, o) => acc + (o.price || o.Price || 0) * (o.quantity || o.Quantity || 0),
          0
        );
        const avgOrderValue =
          totalOrders > 0 ? (totalRevenue / totalOrders).toFixed(2) : 0;

        setSummary({
          total_orders: totalOrders,
          total_revenue: totalRevenue,
          avg_order_value: avgOrderValue,
        });

        // === Продажи по месяцам ===
        const salesMap = {};
        orders.forEach((o) => {
          const orderDate =
            o.orderdate || o.OrderDate || o.order_date || o.order_date_time || "";
          const month = orderDate.slice(0, 7);
          const details = orderDetails.filter(
            (od) =>
              od.order_id === o.id_order ||
              od.order_id === o.ID_Order ||
              od.Order_ID === o.ID_Order
          );
          const revenue = details.reduce(
            (sum, d) =>
              sum +
              (d.price || d.Price || 0) * (d.quantity || d.Quantity || 0),
            0
          );
          salesMap[month] = (salesMap[month] || 0) + revenue;
        });
        setSales(
          Object.entries(salesMap).map(([month, revenue]) => ({ month, revenue }))
        );

        // === Топ категорий (из TopBooksView) ===
        setCategories(
          topBooks.map((b) => ({
            category:
              b.category ||
              b.categoryname ||
              b.CategoryName ||
              b.category_name ||
              "Неизвестно",
            total_sold: b.total_sold || b.TotalSold || b.total || 0,
          }))
        );

        // === Топ пользователей ===
        const topUsers = usersData
          .map((u) => ({
            user:
              u.fullname ||
              u.FullName ||
              `${u.firstname || ""} ${u.lastname || ""}`.trim() ||
              "Неизвестно",
            total_spent: u.total_spent || u.TotalSpent || u.sum_spent || 0,
          }))
          .sort((a, b) => b.total_spent - a.total_spent)
          .slice(0, 5);

        setUsers(topUsers);
      })
      .catch((err) => console.error("Ошибка загрузки представлений:", err))
      .finally(() => setLoading(false));
  }, []);

  // === Экспорт в CSV ===
  const exportCSV = (data, filename) => {
    if (!Array.isArray(data) || data.length === 0) {
      alert("Нет данных для экспорта");
      return;
    }

    const keys = Object.keys(data[0]);
    const csv = [
      keys.join(","),
      ...data.map((row) =>
        keys.map((k) => JSON.stringify(row[k] ?? "")).join(",")
      ),
    ].join("\n");

    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
  };

  if (loading) return <p className="p-6 text-gray-500">Загрузка данных...</p>;

  return (
    <div className="p-6">
      <Header
        title={"Отчеты и аналитика"}
        description={
          "Здесь представлены графики и статистика. Можно также экспортировать данные в CSV."
        }
      />

      {/* === Сводная статистика === */}
      <div className="grid grid-cols-3 gap-4 mb-8">
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">Всего заказов</p>
          <h2 className="text-2xl font-semibold">
            {summary.total_orders || 0}
          </h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">Общая выручка</p>
          <h2 className="text-2xl font-semibold">
            {summary.total_revenue || 0} ₽
          </h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">Средний чек</p>
          <h2 className="text-2xl font-semibold">
            {summary.avg_order_value || 0} ₽
          </h2>
        </div>
      </div>

      {/* === Диаграммы === */}
      <div className="grid grid-cols-2 gap-6">
        {/* Продажи по месяцам */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">Продажи по месяцам</h3>
          <Line
            data={{
              labels: sales.map((s) => s.month),
              datasets: [
                {
                  label: "Выручка (₽)",
                  data: sales.map((s) => s.revenue),
                  borderColor: "#3b82f6",
                  backgroundColor: "rgba(59,130,246,0.2)",
                  tension: 0.3,
                },
              ],
            }}
          />
        </div>

        {/* Топ категорий */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">Топ категорий</h3>
          <Pie
            data={{
              labels: categories.map((c) => c.category),
              datasets: [
                {
                  data: categories.map((c) => c.total_sold),
                  backgroundColor: [
                    "#60a5fa",
                    "#34d399",
                    "#fbbf24",
                    "#f87171",
                    "#a78bfa",
                  ],
                },
              ],
            }}
          />
        </div>

        {/* Топ пользователей */}
        <div className="bg-white p-4 rounded-xl shadow col-span-2">
          <h3 className="text-lg font-semibold mb-2">Топ пользователей</h3>
          <Bar
            data={{
              labels: users.map((u) => u.user),
              datasets: [
                {
                  label: "Сумма покупок (₽)",
                  data: users.map((u) => u.total_spent),
                  backgroundColor: "#34d399",
                },
              ],
            }}
            options={{
              plugins: {
                legend: { display: false },
              },
            }}
          />
        </div>
      </div>

      {/* === Экспорт === */}
      <button
        disabled={!Array.isArray(sales) || sales.length === 0}
        className="formButton mt-6"
        onClick={() => exportCSV(sales, "sales_report.csv")}
      >
        Экспортировать продажи (CSV)
      </button>
    </div>
  );
};

export default Reports;
