import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header.js';
import Form from '../components/Form.js';

const Register = () => {
  const navigate = useNavigate();

  // поля формы
  const [lastname, setLastname] = useState('');
  const [firstname, setFirstname] = useState('');
  const [patronymic, setPatronymic] = useState('');
  const [email, setEmail] = useState('');
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [roleId, setRoleId] = useState(''); 
  const [roles, setRoles] = useState([]);  

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // загружаем роли при монтировании
  useEffect(() => {
    const fetchRoles = async () => {
      try {
        const res = await axios.get('/api/roles'); 
        setRoles(res.data);
      } catch (err) {
        console.error('Ошибка при загрузке ролей:', err);
      }
    };
    fetchRoles();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      if (!roleId) {
        setError('Пожалуйста, выберите роль.');
        setLoading(false);
        return;
      }

      // создаём пользователя и аккаунт
      await axios.post('/api/users', {
        login,
        password,
        role_id: roleId,
        lastname,
        firstname,
        patronymic,
        email
      });

      // авторизация
      const { data } = await axios.post('/api/accounts/login', {
        login,
        password
      });

      // сохраняем в localStorage
      localStorage.setItem('user', JSON.stringify(data));

      navigate('/');
    } catch (err) {
      console.error(err);
      setError(err?.response?.data?.error || 'Ошибка при регистрации');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Header title="Регистрация" description="Создайте новый аккаунт" />

      <div className="formContainer">
        <Form
          onSubmit={handleSubmit}
          loading={loading}
          error={error}
          submitLabel="Создать аккаунт"
          loadingLabel="Создаём аккаунт..."
        >
          <label className="formLabel">Фамилия</label>
          <input
            className="formInput"
            type="text"
            value={lastname}
            onChange={(e) => setLastname(e.target.value)}
            required
          />

          <label className="formLabel">Имя</label>
          <input
            className="formInput"
            type="text"
            value={firstname}
            onChange={(e) => setFirstname(e.target.value)}
            required
          />

          <label className="formLabel">Отчество</label>
          <input
            className="formInput"
            type="text"
            value={patronymic}
            onChange={(e) => setPatronymic(e.target.value)}
          />

          <label className="formLabel">Электронная почта</label>
          <input
            className="formInput"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

           <label className="formLabel">Роль</label>
          <select
            className="formInput"
            value={roleId}
            onChange={(e) => setRoleId(e.target.value)}
            required
          >
            <option value="">Выберите роль</option>
            {roles.map((role) => (
              <option key={role.id_role} value={role.id_role}>
                {role.rolename}
              </option>
            ))}
          </select>

          <label className="formLabel">Логин</label>
          <input
            className="formInput"
            type="text"
            value={login}
            onChange={(e) => setLogin(e.target.value)}
            required
          />

          <label className="formLabel">Пароль</label>
          <input
            className="formInput"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </Form>

        <div className="hint">
          Уже есть аккаунт?{' '}
          <Link className="redirectLink" to="/login">
            Войти
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
