import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../axiosSetup.js";
import Form from "../components/Form.js";
import Header from "../components/Header.js";

const AddBookPage = () => {
  const navigate = useNavigate();
  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const [form, setForm] = useState({
    title: "",
    description: "",
    publishdate: "",
    author_id: "",
    publisher_id: "",
    category_id: "",
    price: "",
    quantity: "",
    imageurl: "",
  });

  const handleChange = (key, value) => {
    setForm((prev) => ({ ...prev, [key]: value }));
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [authorsRes, categoriesRes] = await Promise.all([
          api.get("/authors"),
          api.get("/categories"),
          api.get("/publishers"),
        ]);
        setAuthors(authorsRes.data);
        setCategories(categoriesRes.data);
      } catch (err) {
        console.error("Ошибка загрузки данных:", err);
        setError("Не удалось загрузить данные");
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const handleEsc = (event) => {
      if (event.key === "Escape") {
        navigate("/products");
      }
    };

    window.addEventListener("keydown", handleEsc);
    return () => window.removeEventListener("keydown", handleEsc);
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      await api.post("/products", form);
      navigate("/products");
    } catch (err) {
      console.error("Ошибка добавления книги:", err);
      setError("Не удалось добавить книгу");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
        <Header title={"Добавить книгу"} description={"Здесь вы можете добавлять товары"}/>
        <div className="formContainer">
            <Form
                onSubmit={handleSubmit}
                loading={loading}
                error={error}
                submitLabel="Сохранить"
                loadingLabel="Сохранение..."
                formClassName="form"
                buttonClassName="formButton"
            >
                <label className="formLabel">Название</label>
                <input
                type="text"
                className="formInput"
                value={form.title}
                onChange={(e) => handleChange("title", e.target.value)}
                required
                />

                <label className="formLabel">Описание</label>
                <textarea
                className="formInput"
                value={form.description}
                onChange={(e) => handleChange("description", e.target.value)}
                rows="3"
                />

                <label className="formLabel">Дата публикации</label>
                <input
                type="date"
                className="formInput"
                value={form.publishdate}
                onChange={(e) => handleChange("publishdate", e.target.value)}
                />

                <label className="formLabel">Автор</label>
                <select
                className="formInput"
                value={form.author_id}
                onChange={(e) => handleChange("author_id", e.target.value)}
                required
                >
                <option value="">Выберите автора</option>
                {authors.map((a) => (
                    <option key={a.id_author} value={a.id_author}>
                    {a.firstname} {a.lastname}
                    </option>
                ))}
                </select>

                <label className="formLabel">Издатель</label>
                <select
                className="formInput"
                value={form.publisher_id}
                onChange={(e) => handleChange("publisher_id", e.target.value)}
                required
                >
                <option value="">Выберите издателя</option>
                {publishers.map((p) => (
                    <option key={p.id_publisher} value={p.id_publisher}>
                    {p.name}
                    </option>
                ))}
                </select>

                <label className="formLabel">Категория</label>
                <select
                className="formInput"
                value={form.category_id}
                onChange={(e) => handleChange("category_id", e.target.value)}
                required
                >
                <option value="">Выберите категорию</option>
                {categories.map((c) => (
                    <option key={c.id_category} value={c.id_category}>
                    {c.name}
                    </option>
                ))}
                </select>

                <label className="formLabel">Цена (₽)</label>
                <input
                type="number"
                className="formInput"
                value={form.price}
                onChange={(e) => handleChange("price", e.target.value)}
                required
                min="0"
                />

                <label className="formLabel">Количество</label>
                <input
                type="number"
                className="formInput"
                value={form.quantity}
                onChange={(e) => handleChange("quantity", e.target.value)}
                required
                min="0"
                />

                <label className="formLabel">URL изображения</label>
                <input
                type="text"
                className="formInput"
                value={form.imageurl}
                onChange={(e) => handleChange("imageurl", e.target.value)}
                placeholder="https://example.com/book.jpg"
                />

                {form.imageurl && (
                <img
                    src={form.imageurl}
                    alt="Обложка"
                    className="card-img-top"
                />
                )}
            </Form>
        </div>
    </div>
  );
};

export default AddBookPage;
