import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const navigate = useNavigate();

  const [activeTab, setActiveTab] = useState("books"); 
  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [publishers, setPublishers] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  const [editingBookId, setEditingBookId] = useState(null);
  const [editedBook, setEditedBook] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [booksRes, authorsRes, publishersRes, categoriesRes] = await Promise.all([
          api.get("/books"),
          api.get("/authors"),
          api.get("/publishers"),
          api.get("/categories"),
        ]);
        setBooks(booksRes.data);
        setAuthors(authorsRes.data);
        setPublishers(publishersRes.data);
        setCategories(categoriesRes.data);
      } catch (err) {
        console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö:", err);
        alert("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleEditClick = (book) => {
    setEditingBookId(book.id_book);
    setEditedBook({ ...book });
  };

  const handleInputChange = (field, value) => {
    setEditedBook((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleSaveChanges = async () => {
    try {
      const data = {
        title: editedBook.title,
        price: editedBook.price,
        quantity: editedBook.quantity,
        description: editedBook.description || "",
        author_id: editedBook.author_id,
        category_id: editedBook.category_id,
        imageurl: editedBook.imageurl,
      };

      await api.put(`/books/${editingBookId}`, data);

      setBooks((prev) =>
        prev.map((b) =>
          b.id_book === editingBookId ? { ...b, ...editedBook } : b
        )
      );

      alert("–ò–∑–º–µ–Ω–µ–Ω–∏—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã.");
      setEditingBookId(null);
      setEditedBook({});
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–π:", err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö.");
    }
  };

  const handleDelete = async (endpoint, id, stateSetter) => {
    if (!window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å –∑–∞–ø–∏—Å—å?")) return;
    try {
      await api.delete(`/${endpoint}/${id}`);
      stateSetter((prev) =>
        prev.filter(
          (item) =>
            item.id_book !== id &&
            item.id_author !== id &&
            item.id_publisher !== id &&
            item.id_category !== id
        )
      );
      alert("–£–¥–∞–ª–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ.");
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏:", err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏.");
    }
  };

  if (loading) {
    return (
      <div>
        <Header title="–ó–∞–≥—Ä—É–∑–∫–∞..." description="–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–¥–æ–∂–¥–∏—Ç–µ" />
      </div>
    );
  }

  return (
    <div>
      <Header title="–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏" description="–ó–¥–µ—Å—å –≤—ã –º–æ–∂–µ—Ç–µ —É–ø—Ä–∞–≤–ª—è—Ç—å —Ç–æ–≤–∞—Ä–∞–º–∏" />

      {/* üîπ –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å */}
      <div className="formContainer tabButtons">
        <button
          className={`menuItem ${activeTab === "books" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("books")}
        >
          –ö–Ω–∏–≥–∏
        </button>
        <button
          className={`menuItem ${activeTab === "authors" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("authors")}
        >
          –ê–≤—Ç–æ—Ä—ã
        </button>
        <button
          className={`menuItem ${activeTab === "publishers" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("publishers")}
        >
          –ò–∑–¥–∞—Ç–µ–ª–∏
        </button>
        <button
          className={`menuItem ${activeTab === "categories" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("categories")}
        >
          –ö–∞—Ç–µ–≥–æ—Ä–∏–∏
        </button>
      </div>

      <div className="formContainer">
        {activeTab === "books" && (
          <button className="menuItem" onClick={() => navigate("/products/add-book")}>
            ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∫–Ω–∏–≥—É
          </button>
        )}
        {activeTab === "authors" && (
          <button className="menuItem" onClick={() => navigate("/products/add-author")}>
            ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞–≤—Ç–æ—Ä–∞
          </button>
        )}
        {activeTab === "publishers" && (
          <button className="menuItem" onClick={() => navigate("/products/add-publisher")}>
            ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∏–∑–¥–∞—Ç–µ–ª—è
          </button>
        )}
        {activeTab === "categories" && (
          <button className="menuItem" onClick={() => navigate("/products/add-category")}>
            ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏—é
          </button>
        )}
      </div>

      <div className="tableContainer">

        {activeTab === "books" && (
          <>
            <h2 className="head">üìö –í—Å–µ –∫–Ω–∏–≥–∏ üìö</h2>
            {books.length === 0 ? (
              <p className="hint">–ö–Ω–∏–≥–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>–û–±–ª–æ–∂–∫–∞</th>
                    <th>–ù–∞–∑–≤–∞–Ω–∏–µ</th>
                    <th>–ê–≤—Ç–æ—Ä</th>
                    <th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
                    <th>–¶–µ–Ω–∞</th>
                    <th>–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ</th>
                    <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {books.map((b) => (
                    <tr key={b.id_book}>
                      <td>{b.id_book}</td>
                      <td>
                        {b.imageurl ? (
                          <img src={b.imageurl} alt={b.title} className="bookImage" />
                        ) : (
                          <div className="noImage">–ù–µ—Ç —Ñ–æ—Ç–æ</div>
                        )}
                      </td>
                      <td>{b.title}</td>
                      <td>{`${b.author_firstname} ${b.author_lastname}`}</td>
                      <td>{b.category_name}</td>
                      <td>{b.price} ‚ÇΩ</td>
                      <td>{b.quantity}</td>
                      <td>
                        <button className="menuItem" onClick={() => handleEditClick(b)}>
                          –ò–∑–º–µ–Ω–∏—Ç—å
                        </button>
                        <button
                          className="menuItemDanger"
                          onClick={() => handleDelete("books", b.id_book, setBooks)}
                        >
                          –£–¥–∞–ª–∏—Ç—å
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}

        {activeTab === "authors" && (
          <>
            <h2 className="head">‚úçÔ∏è –í—Å–µ –∞–≤—Ç–æ—Ä—ã ‚úçÔ∏è</h2>
            {authors.length === 0 ? (
              <p className="hint">–ê–≤—Ç–æ—Ä—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>–§–∞–º–∏–ª–∏—è</th>
                    <th>–ò–º—è</th>
                    <th>–û—Ç—á–µ—Å—Ç–≤–æ</th>
                    <th>–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è</th>
                    <th>–î–∞—Ç–∞ —Å–º–µ—Ä—Ç–∏</th>
                    <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {authors.map((a) => (
                    <tr key={a.id_author}>
                      <td>{a.id_author}</td>
                      <td>{a.lastname}</td>
                      <td>{a.firstname}</td>
                      <td>{a.patronymic || "‚Äî"}</td>
                      <td>
                        {a.birthdate
                          ? new Date(a.birthdate).toLocaleDateString("ru-RU")
                          : "‚Äî"}
                      </td>
                      <td>
                        {a.deathdate
                          ? new Date(a.deathdate).toLocaleDateString("ru-RU")
                          : "‚Äî"}
                      </td>
                      <td>
                        <button
                          className="menuItemDanger"
                          onClick={() => handleDelete("authors", a.id_author, setAuthors)}
                        >
                          –£–¥–∞–ª–∏—Ç—å
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}

        {activeTab === "publishers" && (
          <>
            <h2 className="head">üè¢ –í—Å–µ –∏–∑–¥–∞—Ç–µ–ª–∏ üè¢</h2>
            {publishers.length === 0 ? (
              <p className="hint">–ò–∑–¥–∞—Ç–µ–ª–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>–ù–∞–∑–≤–∞–Ω–∏–µ</th>
                    <th>Email</th>
                    <th>–¢–µ–ª–µ—Ñ–æ–Ω</th>
                    <th>–ê–¥—Ä–µ—Å</th>
                    <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {publishers.map((p) => (
                    <tr key={p.id_publisher}>
                      <td>{p.id_publisher}</td>
                      <td>{p.legalname}</td>
                      <td>{p.email}</td>
                      <td>{p.contactnum}</td>
                      <td>
                        {p.address_id ? (
                          <>
                            {p.indexmail ? `${p.indexmail}, ` : ""}
                            {p.country ? `${p.country}, ` : ""}
                            {p.city ? `–≥. ${p.city}, ` : ""}
                            {p.street ? `—É–ª. ${p.street}, ` : ""}
                            {p.house ? `–¥. ${p.house}` : ""}
                            {p.apartment ? `, –∫–≤. ${p.apartment}` : ""}
                          </>
                        ) : (
                          "‚Äî"
                        )}
                      </td>
                      <td>
                        <button
                          className="menuItemDanger"
                          onClick={() => handleDelete("publishers", p.id_publisher, setPublishers)}
                        >
                          –£–¥–∞–ª–∏—Ç—å
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}

        {/* üìÇ –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ */}
        {activeTab === "categories" && (
          <>
            <h2 className="head">üìÇ –í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ üìÇ</h2>
            {categories.length === 0 ? (
              <p className="hint">–ö–∞—Ç–µ–≥–æ—Ä–∏–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>–ù–∞–∑–≤–∞–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏</th>
                    <th>–û–ø–∏—Å–∞–Ω–∏–µ</th>
                    <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                  </tr>
                </thead>
                <tbody>
                  {categories.map((c) => (
                    <tr key={c.id_category}>
                      <td>{c.id_category}</td>
                      <td>{c.name}</td>
                      <td>{c.description || "‚Äî"}</td>
                      <td>
                        <button
                          className="menuItemDanger"
                          onClick={() => handleDelete("categories", c.id_category, setCategories)}
                        >
                          –£–¥–∞–ª–∏—Ç—å
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </>
        )}
      </div>

      <Footer />
    </div>
  );
};

export default Products;
