import React, { useEffect, useState } from "react";
import api from "../axiosSetup.js";
import Header from "../components/Header.js";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Reports = () => {
  const [topBooks, setTopBooks] = useState([]);
  const [topUsers, setTopUsers] = useState([]);
  const [sales, setSales] = useState([]);

  useEffect(() => {
    Promise.all([
      api.get("/reports/top-books-view"),
      api.get("/reports/usersaccounts-view"),
      api.get("/reports/orderdetails-view"),
    ])
      .then(([booksRes, usersRes, orderDetailsRes]) => {
        const books = booksRes.data || [];
        const users = usersRes.data || [];
        const orderDetails = orderDetailsRes.data || [];

        // --- üìö –¢–æ–ø –∫–Ω–∏–≥ ---
        const formattedBooks = books
          .sort(
            (a, b) =>
              parseInt(b["–ü—Ä–æ–¥–∞–Ω–æ"] || b["–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ"] || 0) -
              parseInt(a["–ü—Ä–æ–¥–∞–Ω–æ"] || a["–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ"] || 0)
          )
          .slice(0, 10)
          .map((b) => ({
            title: b["–ö–Ω–∏–≥–∞"] || b["–ù–∞–∑–≤–∞–Ω–∏–µ"] || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
            sold: parseInt(b["–ü—Ä–æ–¥–∞–Ω–æ"] || b["–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ"] || 0),
          }));
        setTopBooks(formattedBooks);

        // --- üë• –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π ---
        const formattedUsers = users
          .sort(
            (a, b) =>
              parseFloat(b["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0) -
              parseFloat(a["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0)
          )
          .slice(0, 10)
          .map((u) => ({
            user:
              u["–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å"] ||
              `${u["–§–∞–º–∏–ª–∏—è"] || ""} ${u["–ò–º—è"] || ""} ${u["–û—Ç—á–µ—Å—Ç–≤–æ"] || ""}`.trim() ||
              "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
            total_spent: parseFloat(u["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0),
          }));
        setTopUsers(formattedUsers);

        // --- üí∞ –ü—Ä–æ–¥–∞–∂–∏ –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞ ---
        const formattedSales = orderDetails.map((od) => ({
          id: od["–ö–æ–¥ –¥–µ—Ç–∞–ª–∏"],
          order_id: od["–ö–æ–¥ –∑–∞–∫–∞–∑–∞"],
          book: od["–ö–Ω–∏–≥–∞"],
          quantity: od["–ö–æ–ª-–≤–æ"],
          unit_price: od["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"],
          total:
            parseFloat(od["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"] || 0) * parseInt(od["–ö–æ–ª-–≤–æ"] || 0),
        }));
        setSales(formattedSales);
      })
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:", err));
  }, []);

  // --- üì§ –≠–∫—Å–ø–æ—Ä—Ç CSV ---
  const exportCSV = (data, filename) => {
    if (!Array.isArray(data) || data.length === 0) {
      console.warn("–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞");
      return;
    }

    const csv = [
      Object.keys(data[0]).join(","),
      ...data.map((row) =>
        Object.values(row)
          .map((v) => `"${v}"`)
          .join(",")
      ),
    ].join("\n");

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
  };

  return (
    <div className="p-6">
      <Header
        title="–ê–Ω–∞–ª–∏—Ç–∏–∫–∞ –ø—Ä–æ–¥–∞–∂"
        description="–¢–æ–ø –∫–Ω–∏–≥ –∏ –ø–æ–∫—É–ø–∞—Ç–µ–ª–µ–π –ø–æ –ø—Ä–æ–¥–∞–∂–∞–º. –í–æ–∑–º–æ–∂–µ–Ω —ç–∫—Å–ø–æ—Ä—Ç –≤—Å–µ—Ö –ø—Ä–æ–¥–∞–∂ –≤ CSV."
      />

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* --- üìö –¢–æ–ø –∫–Ω–∏–≥ --- */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-3">–¢–æ–ø –∫–Ω–∏–≥ –ø–æ –ø—Ä–æ–¥–∞–∂–∞–º</h3>
          <Bar
            data={{
              labels: topBooks.map((b) => b.title),
              datasets: [
                {
                  label: "–ü—Ä–æ–¥–∞–Ω–æ (—à—Ç.)",
                  data: topBooks.map((b) => b.sold),
                  backgroundColor: "#36A2EB",
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { display: false },
                title: { display: false },
              },
              scales: {
                x: {
                  ticks: { autoSkip: false, maxRotation: 45, minRotation: 45 },
                },
              },
            }}
          />
        </div>

        {/* --- üë• –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π --- */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-3">
            –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –ø–æ —Å—É–º–º–µ –ø–æ–∫—É–ø–æ–∫
          </h3>
          <Bar
            data={{
              labels: topUsers.map((u) => u.user),
              datasets: [
                {
                  label: "–°—É–º–º–∞ –ø–æ–∫—É–ø–æ–∫ (‚ÇΩ)",
                  data: topUsers.map((u) => u.total_spent),
                  backgroundColor: "#4BC0C0",
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { display: false },
                title: { display: false },
              },
              scales: {
                x: {
                  ticks: { autoSkip: false, maxRotation: 45, minRotation: 45 },
                },
              },
            }}
          />
        </div>
      </div>

      {/* --- üì§ –ö–Ω–æ–ø–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞ --- */}
      <div className="mt-6 flex">
        <button
          disabled={!sales.length}
          className="formButton bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 disabled:bg-gray-400"
          onClick={() => exportCSV(sales, "sales_report.csv")}
        >
          –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–¥–∞–∂–∏ (CSV)
        </button>
      </div>
    </div>
  );
};

export default Reports;
