import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../components/CardMain.js';
import Footer from '../components/Footer.js';
import Header from '../components/Header.js';
import { motion } from "framer-motion";
import { getCartFromStorage, saveCartToStorage, addToCartStorage, removeFromCartStorage } from '../utils/cartUtils.js';
import { useBooksData } from '../utils/booksUtils.js';
import { getCurrentUserId } from '../utils/userUtils.js';

const Cart = () => {
  const { data, setData } = useBooksData();
  const [cart, setCart] = useState(getCartFromStorage())

  const handleCartChange = () => {
    useEffect(() => {
      if (getCurrentUserId()) {
        saveCartToStorage(cart);
      }
    }, [cart]);
  }

  const mappedBooks = data.map(book => {
    const inCart = cart.find(c => c.bookId === book.id_book);
    return {
      id: book.id_book,
      name: book.title,
      price: Number(book.price),
      addedAmount: inCart?.quantity || 0,
      addedToCart: !!inCart
    };
  });


  const calculateTotalPrice = () => {
    return mappedBooks.reduce((total, item) => {
      return total + (item.addedAmount * item.price);
    }, 0);
  };

  const BuyOption = () => {
    if (calculateTotalPrice() !== 0)
        {
            return <motion.div
            initial={{x:1000, opacity:0}}
            animate={{x:0, opacity:1}}
            transition={{ delay:0.5, duration:1.5}}
            >
                <u><p className='total'>Общая цена: {calculateTotalPrice()} руб.</p></u>
                <button className='buyButton'onClick={() => Sold()}>Купить</button>
            </motion.div>
        }
  }

  const Sold = async () => {
    try {
      if (!getCurrentUserId()) return;
      if (cart.length === 0) return;

      const orderPayload = {
        user_id: userId,
        status_id: 1,
        deliverytype_id: 1,
        address_id: 1,
        items: cart.map(ci => ({ book_id: ci.bookId, quantity: ci.quantity, price: (mappedBooks.find(d => d.id === ci.bookId)?.price) || 0 }))
      };

      console.log('создание заказа:', orderPayload);
      await axios.post('http://localhost:5000/api/orders', orderPayload);

      console.log(orderPayload);

      localStorage.setItem(`cart:${getCurrentUserId()}`, JSON.stringify([]));
      window.location.reload();
    } catch (err) {
      console.error('заказ не создан:', err?.response?.data || err);
      alert(`Не удалось оформить заказ. Недостаточно экземпляров данной книги на складе.`);
    }
  }

    return (
        <div>
            <Header
                title="Корзина"
                description={ calculateTotalPrice() !== 0 ? "Вы еще не передумали покупать книги? Так купите их прямо сейчас!" : "В корзине пусто."}
            />
            <motion.div
                initial={{x:-1000, opacity:0}}
                animate={{x:0, opacity:1}}
                transition={{ delay:0.5, duration:1.5}}
            >
                <Card
                    term = {d => d.addedToCart === true}
                    route="cart"
                    onCartChange={handleCartChange}
                />
            </motion.div>
            <BuyOption/>  
            <Footer/>
        </div>
    );
}

export default Cart;
