import React, { useEffect, useState } from "react";
import api from "../axiosSetup.js";
import Header from "../components/Header.js";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, ArcElement, Title, Tooltip, Legend);

const Reports = () => {
  const [topBooks, setTopBooks] = useState([]);
  const [topUsers, setTopUsers] = useState([]);
  const [sales, setSales] = useState([]);
  const [booksByCategory, setBooksByCategory] = useState([]); // üîπ –Ω–æ–≤–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ

  useEffect(() => {
    Promise.all([
      api.get("/reports/top-books-view"),
      api.get("/reports/top-users-view"),
      api.get("/reports/orderdetails-view"),
      api.get("/reports/books-view"),
    ])
      .then(([booksRes, usersRes, orderDetailsRes, booksViewRes]) => {
        const books = booksRes.data || [];
        const users = usersRes.data || [];
        const orderDetails = orderDetailsRes.data || [];
        const booksView = booksViewRes.data || [];

        // --- üìö –¢–æ–ø –∫–Ω–∏–≥ ---
        const formattedBooks = books
          .slice(0, 10)
          .map((b) => ({
            title: b["–ù–∞–∑–≤–∞–Ω–∏–µ –∫–Ω–∏–≥–∏"] || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
            sold: parseInt(b["–ö–æ–ª-–≤–æ –æ—Ç–∑—ã–≤–æ–≤"] || 0),
          }));
        setTopBooks(formattedBooks);

        // --- üë• –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π ---
        const formattedUsers = users
          .map((u) => ({
            user: u.username || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
            total_spent: parseFloat(u.total_spent || 0),
          }))
          .sort((a, b) => b.total_spent - a.total_spent)
          .slice(0, 10);
        setTopUsers(formattedUsers);

        // --- üí∞ –ü—Ä–æ–¥–∞–∂–∏ ---
        const formattedSales = orderDetails.map((od) => ({
          id: od["–ö–æ–¥ –¥–µ—Ç–∞–ª–∏"],
          order_id: od["–ö–æ–¥ –∑–∞–∫–∞–∑–∞"],
          book: od["–ö–Ω–∏–≥–∞"],
          quantity: od["–ö–æ–ª-–≤–æ"],
          unit_price: od["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"],
          total:
            parseFloat(od["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"] || 0) * parseInt(od["–ö–æ–ª-–≤–æ"] || 0),
        }));
        setSales(formattedSales);

        // --- üè∑ –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ ---
        const categoryMap = {};
        booksView.forEach((book) => {
          const category = book["–ö–∞—Ç–µ–≥–æ—Ä–∏—è"] || "–ë–µ–∑ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏";
          categoryMap[category] = (categoryMap[category] || 0) + 1;
        });
        const formattedCategories = Object.entries(categoryMap).map(
          ([category, count]) => ({ category, count })
        );
        setBooksByCategory(formattedCategories);
      })
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:", err));
  }, []);


  // --- üì§ –≠–∫—Å–ø–æ—Ä—Ç CSV ---
  const exportCSV = (data, filename) => {
    if (!Array.isArray(data) || data.length === 0) return;
    const csv = [
      Object.keys(data[0]).join(","),
      ...data.map((row) =>
        Object.values(row)
          .map((v) => `"${v}"`)
          .join(",")
      ),
    ].join("\n");

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
  };

  return (
    <div className="reports-container">
      <Header
        title="–ê–Ω–∞–ª–∏—Ç–∏–∫–∞ –ø—Ä–æ–¥–∞–∂"
        description="–¢–æ–ø –∫–Ω–∏–≥, –ø–æ–∫—É–ø–∞—Ç–µ–ª–µ–π –∏ —Ä–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –∫–Ω–∏–≥ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º. –í–æ–∑–º–æ–∂–µ–Ω —ç–∫—Å–ø–æ—Ä—Ç –≤—Å–µ—Ö –ø—Ä–æ–¥–∞–∂ –≤ CSV."
      />

      <div>
        <div className="chart-card">
          <h3 className="chart-title">–¢–æ–ø –∫–Ω–∏–≥ –ø–æ –æ—Ç–∑—ã–≤–∞–º</h3>
          <div className="chart-wrapper-small">
            <Pie
              data={{
                labels: topBooks.map((b) => b.title),
                datasets: [
                  {
                    label: "–ö–æ–ª-–≤–æ –æ—Ç–∑—ã–≤–æ–≤",
                    data: topBooks.map((b) => b.sold),
                    backgroundColor: [
                      "#FF6384",
                      "#36A2EB",
                      "#FFCE56",
                      "#4BC0C0",
                      "#9966FF",
                      "#FF9F40",
                      "#C9CBCF",
                    ],
                    borderColor: "#fff",
                    borderWidth: 2,
                  },
                ],
              }}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    display: true,
                    position: "bottom",
                    labels: { boxWidth: 12, font: { size: 11 } },
                  },
                  tooltip: {
                    callbacks: {
                      label: (context) =>
                        `${context.label}: ${context.parsed} –æ—Ç–∑—ã–≤–æ–≤`,
                    },
                  },
                },
              }}
            />
          </div>
        </div>

        <div className="chart-card">
          <h3 className="chart-title">–¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –ø–æ —Å—É–º–º–µ –ø–æ–∫—É–ø–æ–∫</h3>
          <div className="chart-wrapper">
            <Bar
              data={{
                labels: topUsers.map((u) => u.user),
                datasets: [
                  {
                    label: "–°—É–º–º–∞ –ø–æ–∫—É–ø–æ–∫ (‚ÇΩ)",
                    data: topUsers.map((u) => u.total_spent),
                    backgroundColor: "#4BC0C0",
                  },
                ],
              }}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: { legend: { display: false } },
                scales: {
                  x: {
                    ticks: { autoSkip: false, maxRotation: 45, minRotation: 45 },
                  },
                },
              }}
            />
          </div>
        </div>

        <div className="chart-card md:col-span-2">
          <h3 className="chart-title">–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–Ω–∏–≥ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º</h3>
          <div className="chart-wrapper">
            <Bar
              data={{
                labels: booksByCategory.map((c) => c.category),
                datasets: [
                  {
                    label: "–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–Ω–∏–≥",
                    data: booksByCategory.map((c) => c.count),
                    backgroundColor: "#FF6384",
                  },
                ],
              }}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: { legend: { display: false } },
                scales: {
                  x: {
                    ticks: { autoSkip: false, maxRotation: 45, minRotation: 45 },
                  },
                },
              }}
            />
          </div>
        </div>
      </div>

      <div>
        <button
          className="formButton"
          onClick={() => exportCSV(sales, "sales_report.csv")}
        >
          –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–¥–∞–∂–∏ (CSV)
        </button>
      </div>
    </div>
  );
};

export default Reports;
