import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [books, setBooks] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await api.get("/books");
        setBooks(response.data);
      } catch (err) {
        console.error("Ошибка при загрузке книг:", err);
        alert("Не удалось загрузить список книг.");
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  // удаление книги
  const handleDelete = async (id_book) => {
    if (!window.confirm("Вы уверены, что хотите удалить эту книгу?")) return;
    try {
      await api.delete(`/books/${id_book}`);
      setBooks((prev) => prev.filter((b) => b.id_book !== id_book));
      alert("Книга удалена.");
    } catch (err) {
      console.error("Ошибка при удалении книги:", err);
      alert("Ошибка при удалении книги.");
    }
  };

  if (loading) {
    return (
      <div>
        <Header title="Книги" description="Загрузка данных..." />
      </div>
    );
  }

  return (
    <div>
      <Header title="Все книги" description="Список всех товаров" />

      <div className="formContainer">
        <button className="menuItem" onClick={() => navigate("/books/add")}>
          ➕ Добавить книгу
        </button>
      </div>

      {books.length === 0 ? (
        <p className="hint">Книги не найдены.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Обложка</th>
              <th>Название</th>
              <th>Автор</th>
              <th>Категория</th>
              <th>Цена</th>
              <th>Количество</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {books.map((b) => (
              <tr key={b.id_book}>
                <td>{b.id_book}</td>

                {/* Обложка книги */}
                <td>
                  {b.image_url ? (
                    <img
                      src={b.image_url.startsWith("http") ? b.image_url : `http://localhost:5000/${b.image_url}`}
                      alt={b.title}
                      style={{
                        width: "60px",
                        height: "80px",
                        objectFit: "cover",
                        borderRadius: "6px",
                        boxShadow: "0 0 4px rgba(0,0,0,0.2)",
                      }}
                    />
                  ) : (
                    <div
                      style={{
                        width: "60px",
                        height: "80px",
                        borderRadius: "6px",
                        backgroundColor: "#f3f3f3",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        color: "#999",
                        fontSize: "12px",
                      }}
                    >
                      Нет фото
                    </div>
                  )}
                </td>

                <td>{b.title}</td>
                <td>
                  {b.author_firstname} {b.author_lastname}
                </td>
                <td>{b.category_name}</td>
                <td>{b.price} ₽</td>
                <td>{b.quantity}</td>
                <td>
                  <button
                    className="menuItem"
                    onClick={() => navigate(`/books/${b.id_book}/edit`)}
                  >
                    Изменить
                  </button>
                  <button
                    className="menuItemDanger"
                    onClick={() => handleDelete(b.id_book)}
                  >
                    Удалить
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <Footer />
    </div>
  );
};

export default Products;
