import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const navigate = useNavigate();

  const [activeTab, setActiveTab] = useState("books");
  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [publishers, setPublishers] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  // üîπ –û–±—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
  const [editingId, setEditingId] = useState(null);
  const [editedItem, setEditedItem] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [booksRes, authorsRes, publishersRes, categoriesRes] =
          await Promise.all([
            api.get("/books"),
            api.get("/authors"),
            api.get("/publishers"),
            api.get("/categories"),
          ]);
        setBooks(booksRes.data);
        setAuthors(authorsRes.data);
        setPublishers(publishersRes.data);
        setCategories(categoriesRes.data);
      } catch (err) {
        console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö:", err);
        alert("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleEditClick = (item) => {
    setEditingId(item.id_book || item.id_author || item.id_publisher || item.id_category);
    setEditedItem({ ...item });
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditedItem({});
  };

  const handleInputChange = (field, value) => {
    setEditedItem((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleSaveChanges = async (type) => {
    try {
      let endpoint = "";
      let data = {};

      switch (type) {
        case "books":
          endpoint = `/books/${editedItem.id_book}`;
          data = {
            title: editedItem.title,
            price: editedItem.price,
            quantity: editedItem.quantity,
            description: editedItem.description || "",
            author_id: editedItem.author_id,
            category_id: editedItem.category_id,
            imageurl: editedItem.imageurl,
          };
          await api.put(endpoint, data);
          setBooks((prev) =>
            prev.map((b) =>
              b.id_book === editedItem.id_book ? { ...b, ...editedItem } : b
            )
          );
          break;

        case "authors":
          endpoint = `/authors/${editedItem.id_author}`;
          data = {
            firstname: editedItem.firstname,
            lastname: editedItem.lastname,
            patronymic: editedItem.patronymic,
            birthdate: editedItem.birthdate,
            deathdate: editedItem.deathdate,
          };
          await api.put(endpoint, data);
          setAuthors((prev) =>
            prev.map((a) =>
              a.id_author === editedItem.id_author ? { ...a, ...editedItem } : a
            )
          );
          break;

        case "publishers":
          endpoint = `/publishers/${editedItem.id_publisher}`;
          data = {
            legalname: editedItem.legalname,
            contactnum: editedItem.contactnum,
            email: editedItem.email,
          };
          await api.put(endpoint, data);
          setPublishers((prev) =>
            prev.map((p) =>
              p.id_publisher === editedItem.id_publisher ? { ...p, ...editedItem } : p
            )
          );
          break;

        case "categories":
          endpoint = `/categories/${editedItem.id_category}`;
          data = { name: editedItem.name };
          await api.put(endpoint, data);
          setCategories((prev) =>
            prev.map((c) =>
              c.id_category === editedItem.id_category ? { ...c, ...editedItem } : c
            )
          );
          break;
      }

      alert("–ò–∑–º–µ–Ω–µ–Ω–∏—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã.");
      handleCancelEdit();
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏:", err);
      alert("–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏—è.");
    }
  };

  const handleDelete = async (endpoint, id, stateSetter) => {
    if (!window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å –∑–∞–ø–∏—Å—å?")) return;
    try {
      await api.delete(`/${endpoint}/${id}`);
      stateSetter((prev) =>
        prev.filter(
          (item) =>
            item.id_book !== id &&
            item.id_author !== id &&
            item.id_publisher !== id &&
            item.id_category !== id
        )
      );
      alert("–£–¥–∞–ª–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ.");
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏:", err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏.");
    }
  };

  if (loading) {
    return <Header title="–ó–∞–≥—Ä—É–∑–∫–∞..." description="–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–¥–æ–∂–¥–∏—Ç–µ" />;
  }

  return (
    <div>
      <Header title="–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏" description="–ó–¥–µ—Å—å –≤—ã –º–æ–∂–µ—Ç–µ —É–ø—Ä–∞–≤–ª—è—Ç—å —Ç–æ–≤–∞—Ä–∞–º–∏" />

      {/* üîπ –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å */}
      <div className="formContainer tabButtons">
        {["books", "authors", "publishers", "categories"].map((tab) => (
          <button
            key={tab}
            className={`menuItem ${activeTab === tab ? "activeTab" : ""}`}
            onClick={() => setActiveTab(tab)}
          >
            {{
              books: "–ö–Ω–∏–≥–∏",
              authors: "–ê–≤—Ç–æ—Ä—ã",
              publishers: "–ò–∑–¥–∞—Ç–µ–ª–∏",
              categories: "–ö–∞—Ç–µ–≥–æ—Ä–∏–∏",
            }[tab]}
          </button>
        ))}
      </div>

      <div className="tableContainer">
        {/* üìö –ö–Ω–∏–≥–∏ */}
        {activeTab === "books" && (
          <>
            <h2 className="head">üìö –í—Å–µ –∫–Ω–∏–≥–∏ üìö</h2>
            <button className="menuItem" onClick={() => navigate("/products/add-book")}>
              ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∫–Ω–∏–≥—É
            </button>
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>–ù–∞–∑–≤–∞–Ω–∏–µ</th>
                  <th>–¶–µ–Ω–∞</th>
                  <th>–ö–æ–ª-–≤–æ</th>
                  <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                </tr>
              </thead>
              <tbody>
                {books.map((b) =>
                  editingId === b.id_book ? (
                    <tr key={b.id_book}>
                      <td>{b.id_book}</td>
                      <td>
                        <input
                          value={editedItem.title}
                          onChange={(e) => handleInputChange("title", e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="number"
                          value={editedItem.price}
                          onChange={(e) => handleInputChange("price", e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="number"
                          value={editedItem.quantity}
                          onChange={(e) => handleInputChange("quantity", e.target.value)}
                        />
                      </td>
                      <td>
                        <button onClick={() => handleSaveChanges("books")}>üíæ</button>
                        <button onClick={handleCancelEdit}>‚ùå</button>
                      </td>
                    </tr>
                  ) : (
                    <tr key={b.id_book}>
                      <td>{b.id_book}</td>
                      <td>{b.title}</td>
                      <td>{b.price} ‚ÇΩ</td>
                      <td>{b.quantity}</td>
                      <td>
                        <button onClick={() => handleEditClick(b)}>‚úèÔ∏è</button>
                        <button
                          className="menuItemDanger"
                          onClick={() => handleDelete("books", b.id_book, setBooks)}
                        >
                          üóë
                        </button>
                      </td>
                    </tr>
                  )
                )}
              </tbody>
            </table>
          </>
        )}

        {/* ‚úçÔ∏è –ê–≤—Ç–æ—Ä—ã */}
        {activeTab === "authors" && (
          <>
            <h2 className="head">‚úçÔ∏è –í—Å–µ –∞–≤—Ç–æ—Ä—ã ‚úçÔ∏è</h2>
            <button className="menuItem" onClick={() => navigate("/products/add-author")}>
              ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∞–≤—Ç–æ—Ä–∞
            </button>
            <table className="table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>–§–∞–º–∏–ª–∏—è</th>
                  <th>–ò–º—è</th>
                  <th>–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è</th>
                  <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                </tr>
              </thead>
              <tbody>
                {authors.map((a) =>
                  editingId === a.id_author ? (
                    <tr key={a.id_author}>
                      <td>{a.id_author}</td>
                      <td>
                        <input
                          value={editedItem.lastname}
                          onChange={(e) => handleInputChange("lastname", e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          value={editedItem.firstname}
                          onChange={(e) => handleInputChange("firstname", e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="date"
                          value={editedItem.birthdate?.slice(0, 10) || ""}
                          onChange={(e) => handleInputChange("birthdate", e.target.value)}
                        />
                      </td>
                      <td>
                        <button onClick={() => handleSaveChanges("authors")}>üíæ</button>
                        <button onClick={handleCancelEdit}>‚ùå</button>
                      </td>
                    </tr>
                  ) : (
                    <tr key={a.id_author}>
                      <td>{a.id_author}</td>
                      <td>{a.lastname}</td>
                      <td>{a.firstname}</td>
                      <td>
                        {a.birthdate
                          ? new Date(a.birthdate).toLocaleDateString("ru-RU")
                          : "‚Äî"}
                      </td>
                      <td>
