import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCurrentUser } from '../utils/userUtils.js';
import { getCartFromStorage } from '../utils/cartUtils.js';

const Header = ({ title, description }) => {
  const navigate = useNavigate();
  const menuRef = useRef(null);

  const user = getCurrentUser();

  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAdminOpen, setIsAdminOpen] = useState(false);
  const [isCartNotEmpty, setIsCartNotEmpty] = useState(getCartFromStorage().length > 0);

  const handleButtonClick = () => navigate('/cart');

  useEffect(() => {
    const handleStorageChange = () => {
      const cart = getCartFromStorage();
      setIsCartNotEmpty(cart.length > 0);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen((prev) => !prev);
    setIsAdminOpen(false); // –∑–∞–∫—Ä—ã–≤–∞–µ–º –ø–æ–¥–º–µ–Ω—é –ø—Ä–∏ —Å–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏–∏ –æ—Å–Ω–æ–≤–Ω–æ–≥–æ –º–µ–Ω—é
  };

  const toggleAdminMenu = () => {
    setIsAdminOpen((prev) => !prev);
  };

  const handleNavigate = (path) => {
    setIsMenuOpen(false);
    setIsAdminOpen(false);
    navigate(path);
  };

  const handleLogout = () => {
    setIsMenuOpen(false);
    setIsAdminOpen(false);
    localStorage.removeItem('user');
    navigate('/');
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
        setIsAdminOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div>
      <div className="mainpage">
        <div className="menuWrapper" ref={menuRef}>
          <button
            className="menuButton"
            onClick={toggleMenu}
            aria-haspopup="true"
            aria-expanded={isMenuOpen}
          >
            ‚öô
          </button>

          {isMenuOpen && (
            <div className="menuDropdown" role="menu">
              <button
                className="menuItem"
                onClick={() => handleNavigate('/account')}
                role="menuitem"
              >
                –£—á–µ—Ç–Ω–∞—è –∑–∞–ø–∏—Å—å
              </button>

              <button
                className="menuItem"
                onClick={() =>
                  handleNavigate(user.role_id === 1 ? '/reports' : '/orders')
                }
                role="menuitem"
              >
                {user.role_id === 1 ? '–û—Ç—á—ë—Ç—ã' : '–ó–∞–∫–∞–∑—ã'}
              </button>

              {user.role_id === 1 && (
                <div className="submenuWrapper">
                  <button
                    className="menuItem submenuToggle"
                    onClick={toggleAdminMenu}
                    aria-expanded={isAdminOpen}
                  >
                    –ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω–∏–µ {isAdminOpen ? '‚ñ≤' : '‚ñº'}
                  </button>

                  {isAdminOpen && (
                    <div className="submenuDropdown" role="menu">
                      <button
                        className="menuItem"
                        onClick={() => handleNavigate('/products')}
                        role="menuitem"
                      >
                        –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏
                      </button>
                      <button
                        className="menuItem"
                        onClick={() => handleNavigate('/orders-manage')}
                        role="menuitem"
                      >
                        –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∑–∞–∫–∞–∑–∞–º–∏
                      </button>
                      <button
                        className="menuItem"
                        onClick={() => handleNavigate('/all-accounts')}
                        role="menuitem"
                      >
                        –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∞–∫–∫–∞—É–Ω—Ç–∞–º–∏
                      </button>
                      <button
                        className="menuItem"
                        onClick={() => handleNavigate('/audit')}
                        role="menuitem"
                      >
                        –ñ—É—Ä–Ω–∞–ª –∞—É–¥–∏—Ç–∞
                      </button>
                      <button
                        className="menuItem"
                        onClick={() => handleNavigate('/backup-db')}
                        role="menuitem"
                      >
                        –°–æ–∑–¥–∞–Ω–∏–µ —Ä–µ–∑–µ—Ä–≤–Ω–æ–π –∫–æ–ø–∏–∏
                      </button>
                    </div>
                  )}
                </div>
              )}

              <button
                className="menuItem menuItemDanger"
                onClick={handleLogout}
                role="menuitem"
              >
                –í—ã–π—Ç–∏
              </button>
            </div>
          )}
        </div>

        <header className="welcome">{title}</header>
        <button
          className={isCartNotEmpty ? 'cartFull' : 'cartEmpty'}
          onClick={handleButtonClick}
        >
          üõí
        </button>
      </div>

      <h2 className="find">{description}</h2>
    </div>
  );
};

export default Header;
