import React, { useEffect, useState } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import api from '../axiosSetup.js';

const Reports = () => {
  const [summary, setSummary] = useState({});
  const [sales, setSales] = useState([]);
  const [categories, setCategories] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
  Promise.all([
    api.get("/reports/orders-view"),
    api.get("/reports/books-view"),
    api.get("/reports/top-books-view"),
    api.get("/reports/orderdetails-view"),
    api.get("/reports/usersaccounts-view")
  ])
  .then(([ordersRes, booksRes, topBooksRes, orderDetailsRes, usersRes]) => {
    const orders = ordersRes.data;
    const books = booksRes.data;
    const topBooks = topBooksRes.data;
    const orderDetails = orderDetailsRes.data;
    const users = usersRes.data;

    const totalOrders = orders.length;
    const totalRevenue = orderDetails.reduce((acc, o) => acc + o.price * o.quantity, 0);
    const avgOrderValue = totalOrders > 0 ? (totalRevenue / totalOrders).toFixed(2) : 0;
    setSummary({ total_orders: totalOrders, total_revenue: totalRevenue, avg_order_value: avgOrderValue });

    const salesMap = {};
    orders.forEach(o => {
      const month = o.orderdate?.slice(0, 7);
      const details = orderDetails.filter(od => od.order_id === o.id_order);
      const revenue = details.reduce((sum, d) => sum + d.price * d.quantity, 0);
      salesMap[month] = (salesMap[month] || 0) + revenue;
    });
    setSales(Object.entries(salesMap).map(([month, revenue]) => ({ month, revenue })));

    setCategories(topBooks);

    const topUsers = users
      .sort((a, b) => b.total_spent - a.total_spent)
      .slice(0, 5)
      .map(u => ({ user: u.fullname, total_spent: u.total_spent }));
    setUsers(topUsers);
  })
  .catch(err => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω–∏–π:", err));
}, []);

  const exportCSV = (data, filename) => {
    const csv = [
      Object.keys(data[0]).join(","),
      ...data.map(row => Object.values(row).join(","))
    ].join("\n");
    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">üìà –û—Ç—á—ë—Ç—ã –∏ –∞–Ω–∞–ª–∏—Ç–∏–∫–∞</h1>

      <div className="grid grid-cols-3 gap-4 mb-8">
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–í—Å–µ–≥–æ –∑–∞–∫–∞–∑–æ–≤</p>
          <h2 className="text-2xl font-semibold">{summary.total_orders}</h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–û–±—â–∞—è –≤—ã—Ä—É—á–∫–∞</p>
          <h2 className="text-2xl font-semibold">{summary.total_revenue} ‚ÇΩ</h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–°—Ä–µ–¥–Ω–∏–π —á–µ–∫</p>
          <h2 className="text-2xl font-semibold">{summary.avg_order_value} ‚ÇΩ</h2>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-6">
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">–ü—Ä–æ–¥–∞–∂–∏ –ø–æ –º–µ—Å—è—Ü–∞–º</h3>
          <Line
            data={{
              labels: sales.map(s => s.month),
              datasets: [{ label: "–í—ã—Ä—É—á–∫–∞", data: sales.map(s => s.revenue) }]
            }}
          />
        </div>

        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">–¢–æ–ø –∫–∞—Ç–µ–≥–æ—Ä–∏–π</h3>
          <Pie
            data={{
              labels: categories.map(c => c.category),
              datasets: [{ data: categories.map(c => c.total_sold) }]
            }}
          />
        </div>

        <div className="bg-white p-4 rounded-xl shadow col-span-2">
          <h3 className="text-lg font-semibold mb-2">–¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π</h3>
          <Bar
            data={{
              labels: users.map(u => u.user),
              datasets: [{ label: "–°—É–º–º–∞ –ø–æ–∫—É–ø–æ–∫", data: users.map(u => u.total_spent) }]
            }}
          />
        </div>
      </div>

    <button
        disabled={!Array.isArray(sales) || sales.length === 0}
        className="formButton"
        onClick={() => exportCSV(sales, "sales_report.csv")}
        >–≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–¥–∞–∂–∏ (CSV)
    </button>
    </div>
  );
}

export default Reports;
