import React, { useMemo } from 'react';
import { useNavigate, useState, Link } from 'react-router-dom';
import Header from '../components/Header.js';

const Account = () =>
{
  const navigate = useNavigate();

  const storedUser = useMemo(() => {
    try {
      const userString = localStorage.getItem('user');
      console.log(JSON.parse(userString));
      return userString ? JSON.parse(userString) : null;
    } catch (e) {
      return null;
    }
  }, []);

  const [isEditing, setIsEditing] = useState(false);
  const [login, setLogin] = useState(storedUser.login);
  const [email, setEmail] = useState(storedUser.email);
  const [lastname, setLastname] = useState(storedUser.lastname);
  const [firstname, setFirstname] = useState(storedUser.firstname);
  const [patronymic, setPatronymic] = useState(storedUser.patronymic)

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleBlur = () => {
    setIsEditing(false);
    // здесь можно добавить запрос к серверу для сохранения нового логина
    // например: axios.put(`/api/users/${storedUser.id}`, { login });
  };

  if (!storedUser)
  {
    return (
      <div>
        <Header title={'Учетная запись'} description={'Вы не вошли в систему'} />
        <div className="formContainer">
          <div className="hint">
            Пожалуйста, <Link className='redirectLink' to="/login">войдите</Link> или <Link className='redirectLink' to="/register">зарегистрируйтесь</Link>.
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header title={'Учетная запись'} description={'Данные вашего профиля'} />
      <table class="table">
        <thead>
        </thead>
        <tbody>
          <tr>
            <th scope="row">Логин</th>
              <td>
                {isEditing ? (
                  <input
                    type="text"
                    value={login}
                    onChange={(e) => setLogin(e.target.value)}
                    onBlur={handleBlur}
                    autoFocus
                  />) : ( login )}
              </td>
              <td>
                <button className="menuItem" onClick={handleEditClick}>
                  Изменить
                </button>
              </td>
          </tr>
          <tr>
            <th scope="row">Пароль</th>
            <td></td>
            <td>
              <button className='menuItemDanger'>Сбросить</button>
            </td>
          </tr>
          <tr>
            <th scope="row">Эл. почта</th>
              <td>
                {isEditing ? (
                  <input
                    type="text"
                    value={email}
                    onChange={(e) => setLogin(e.target.value)}
                    onBlur={handleBlur}
                    autoFocus
                  />) : ( email )}
              </td>
              <td>
                <button className="menuItem" onClick={handleEditClick}>
                  Изменить
                </button>
              </td>
          </tr>
          <tr>
            <th scope="row">Фамилия</th>
              <td>
                {isEditing ? (
                  <input
                    type="text"
                    value={lastname}
                    onChange={(e) => setLogin(e.target.value)}
                    onBlur={handleBlur}
                    autoFocus
                  />) : ( lastname )}
              </td>
              <td>
                <button className="menuItem" onClick={handleEditClick}>
                  Изменить
                </button>
              </td>
          </tr>
          <tr>
            <th scope="row">Имя</th>
              <td>
                {isEditing ? (
                  <input
                    type="text"
                    value={firstname}
                    onChange={(e) => setLogin(e.target.value)}
                    onBlur={handleBlur}
                    autoFocus
                  />) : ( firstname )}
              </td>
              <td>
                <button className="menuItem" onClick={handleEditClick}>
                  Изменить
                </button>
              </td>
          </tr>
          <tr>
            <th scope="row">Отчество</th>
              <td>
                {isEditing ? (
                  <input
                    type="text"
                    value={patronymic}
                    onChange={(e) => setLogin(e.target.value)}
                    onBlur={handleBlur}
                    autoFocus
                  />) : ( patronymic )}
              </td>
              <td>
                <button className="menuItem" onClick={handleEditClick}>
                  Изменить
                </button>
              </td>
          </tr>
          <tr>
            <th scope="row">Роль</th>
            <td>{storedUser.rolename}</td>
            <td></td>
          </tr>
        </tbody>
      </table>

    </div>
  );
}

export default Account;
