import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../axiosSetup.js";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";

const AddProduct = () => {
  const navigate = useNavigate();

  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [form, setForm] = useState({
    title: "",
    description: "",
    publishdate: "",
    author_id: "",
    publisher_id: "",
    category_id: "",
    price: "",
    quantity: "",
    imageurl: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [authorsRes, categoriesRes] = await Promise.all([
          api.get("/authors"),
          api.get("/categories"),
        ]);
        setAuthors(authorsRes.data);
        setCategories(categoriesRes.data);
      } catch (err) {
        console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∞–≤—Ç–æ—Ä–æ–≤ –∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–π:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (field, value) => {
    setForm((prev) => ({ ...prev, [field]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await api.post("/books", form);
      alert("–ö–Ω–∏–≥–∞ —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–∞!");
      navigate("/products"); // –≤–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —Å–æ —Å–ø–∏—Å–∫–æ–º
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ –∫–Ω–∏–≥–∏:", err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ –∫–Ω–∏–≥–∏.");
    }
  };

  return (
    <div>
        <Header title="–î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∫–Ω–∏–≥–∏" description="–°–æ–∑–¥–∞–π—Ç–µ –Ω–æ–≤—É—é –∑–∞–ø–∏—Å—å –æ —Ç–æ–≤–∞—Ä–µ" />

        <form className="formContainer" onSubmit={handleSubmit}>
            <label className="formLabel">–ù–∞–∑–≤–∞–Ω–∏–µ</label>
            <input
            type="text"
            className="formInput"
            value={form.title}
            onChange={(e) => handleChange("title", e.target.value)}
            required
            />

            <label className="formLabel">–û–ø–∏—Å–∞–Ω–∏–µ</label>
            <textarea
            className="formInput"
            value={form.description}
            onChange={(e) => handleChange("description", e.target.value)}
            rows="3"
            />

            <label className="formLabel">–î–∞—Ç–∞ –ø—É–±–ª–∏–∫–∞—Ü–∏–∏</label>
            <input
            type="date"
            className="formInput"
            value={form.publishdate}
            onChange={(e) => handleChange("publishdate", e.target.value)}
            />

            <label className="formLabel">–ê–≤—Ç–æ—Ä</label>
            <select
            className="formInput"
            value={form.author_id}
            onChange={(e) => handleChange("author_id", e.target.value)}
            required
            >
            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –∞–≤—Ç–æ—Ä–∞</option>
            {authors.map((a) => (
                <option key={a.id_author} value={a.id_author}>
                {a.firstname} {a.lastname}
                </option>
            ))}
            </select>

            <label className="formLabel">–ö–∞—Ç–µ–≥–æ—Ä–∏—è</label>
            <select
            className="formInput"
            value={form.category_id}
            onChange={(e) => handleChange("category_id", e.target.value)}
            required
            >
            <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é</option>
            {categories.map((c) => (
                <option key={c.id_category} value={c.id_category}>
                {c.name}
                </option>
            ))}
            </select>

            <label className="formLabel">–¶–µ–Ω–∞ (‚ÇΩ)</label>
            <input
            type="number"
            className="formInput"
            value={form.price}
            onChange={(e) => handleChange("price", e.target.value)}
            required
            min="0"
            />

            <label className="formLabel">–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ</label>
            <input
            type="number"
            className="formInput"
            value={form.quantity}
            onChange={(e) => handleChange("quantity", e.target.value)}
            required
            min="0"
            />

            <label className="formLabel">URL –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è</label>
            <input
            type="text"
            className="formInput"
            value={form.imageurl}
            onChange={(e) => handleChange("imageurl", e.target.value)}
            placeholder="https://example.com/book.jpg"
            />

            {form.imageurl && (
            <img
                src={form.imageurl}
                alt="–û–±–ª–æ–∂–∫–∞"
                className="bookImage"
                style={{ marginTop: "10px" }}
            />
            )}

            <div className="formActions">
            <button type="submit" className="menuItem">
                üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
            </button>
            <button
                type="button"
                className="menuItemDanger"
                onClick={() => navigate("/products")}
            >
                ‚ùå –û—Ç–º–µ–Ω–∞
            </button>
            </div>
      </form>

      <Footer />
    </div>
  );
};

export default AddProduct;
