import React, { useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Header from '../components/Header.js';

const Account = () =>
{
  const navigate = useNavigate();

  const storedUser = useMemo(() => {
    try {
      const userString = localStorage.getItem('user');
      return userString ? JSON.parse(userString) : null;
    } catch (e) {
      return null;
    }
  }, []);

  if (!storedUser)
  {
    return (
      <div>
        <Header title={'Учетная запись'} description={'Вы не вошли в систему'} />
        <div className="formContainer">
          <div className="hint">
            Пожалуйста, <Link className='redirectLink' to="/login">войдите</Link> или <Link className='redirectLink' to="/register">зарегистрируйтесь</Link>.
          </div>
        </div>
      </div>
    );
  }

  const fullName = [storedUser.firstname, storedUser.lastname].filter(Boolean).join(' ');

  return (
    <div>
      <Header title={'Учетная запись'} description={'Ваши данные профиля'} />
      <div className="formContainer">
        <div className="profileCard">
          <div className="profileRow"><span className="profileLabel">Логин:</span><span className="profileValue">{storedUser.login || '—'}</span></div>
          <div className="profileRow"><span className="profileLabel">Имя:</span><span className="profileValue">{fullName || '—'}</span></div>
          <div className="profileRow"><span className="profileLabel">Email:</span><span className="profileValue">{storedUser.email || '—'}</span></div>
          <div className="profileRow"><span className="profileLabel">Роль:</span><span className="profileValue">{storedUser.rolename || storedUser.role || storedUser.role_id || '—'}</span></div>
        </div>

        <div className="accountActions">
          <button className="menuItem" onClick={() => navigate('/orders')}>Мои заказы</button>
          <button className="menuItem" onClick={() => navigate('/settings')}>Настройки</button>
        </div>
      </div>
    </div>
  );
}

export default Account;
