import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CardItem from './CardItem.js';

const Card = ({ head, term, route }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/books')
      .then(res => {
        const mapped = (res.data || []).map(row => ({
          id: row.id_book,
          name: row.title,
          author: row.author,
          price: Number(row.price),
          url: row.imageurl,
          genre: row.category,
          addedToCart: false,
          addedAmount: 0,
          isFavorite: false
        }));
        setData(mapped);
      })
      .catch(err => console.error("Ошибка при загрузке книг:", err));
  }, []);

  const addToCart = (id) => {
    setData(prev => prev.map(item =>
      item.id === id
        ? { ...item, addedToCart: true, addedAmount: item.addedAmount + 1 }
        : item
    ));
  };

  const removeFromCart = (id) => {
    setData(prev => prev.map(item =>
      item.id === id
        ? {
            ...item,
            addedAmount: Math.max(item.addedAmount - 1, 0),
            addedToCart: item.addedAmount - 1 > 0
          }
        : item
    ));
  };

  const becomeFavorite = (id) => {
    setData(prev => prev.map(item =>
      item.id === id ? { ...item, isFavorite: !item.isFavorite } : item
    ));
  };


  const filteredData = typeof term === 'function' ? data.filter(term) : data;

  return (
    <div className="cardSection">
      <u><header className="head">{head}</header></u>
      <div className="cards">
        {filteredData.length > 0 ? (
          filteredData.map((item) => (
            <CardItem
              key={item.id}
              item={item}
              becomeFavorite={becomeFavorite}
              addToCart={addToCart}
              removeFromCart={removeFromCart}
              changePrice={route === "cart"}
            />
          ))
        ) : (
          <p className='find'>
            Нет книг.
          </p>
        )}
      </div>
    </div>
  );
};

export default Card;
