import React, { useEffect, useState } from 'react';
import Header from '../components/Header.js';
import Footer from '../components/Footer.js';
import api from '../axiosSetup.js';
import { Link } from 'react-router-dom';
import { getCurrentUser } from '../utils/userUtils.js';

const Accounts = () => {
  const [accounts, setAccounts] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({ role_id: '' });
  const [deletingId, setDeletingId] = useState(null);

  const fetchAccounts = async () => {
    try {
      const res = await api.get('/accounts');
      setAccounts(res.data || []);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∞–∫–∫–∞—É–Ω—Ç–æ–≤:', err);
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∞–∫–∫–∞—É–Ω—Ç—ã.');
    } finally {
      setLoading(false);
    }
  };

  const fetchRoles = async () => {
    try {
      const res = await api.get('/roles');
      setRoles(res.data || []);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ä–æ–ª–µ–π:', err);
    }
  };

  useEffect(() => {
    fetchAccounts();
    fetchRoles();
  }, []);

  const handleEdit = (account) => {
    setEditingId(account.id_account);
    setEditData({
      login: account.login,
      role_id: account.role_id,
      password: '',
    });
  };

  const handleSave = async (id) => {
    try {
      await api.patch(`/accounts/${id}`, editData);
      await fetchAccounts();
      setEditingId(null);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –∞–∫–∫–∞—É–Ω—Ç–∞:', err);
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å –∞–∫–∫–∞—É–Ω—Ç.');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('–£–¥–∞–ª–∏—Ç—å —ç—Ç–æ—Ç –∞–∫–∫–∞—É–Ω—Ç?')) return;
    try {
      setDeletingId(id);
      await api.delete(`/accounts/${id}`);
      await fetchAccounts();
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –∞–∫–∫–∞—É–Ω—Ç–∞:', err);
      setError('–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å –∞–∫–∫–∞—É–Ω—Ç.');
    } finally {
      setDeletingId(null);
    }
  };

  if (!getCurrentUser()) {
    return (
      <div>
        <Header title="–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∞–∫–∫–∞—É–Ω—Ç–∞–º–∏" description="–í—ã –Ω–µ –≤–æ—à–ª–∏ –≤ —Å–∏—Å—Ç–µ–º—É" />
        <div className="formContainer">
          <div className="hint">
            –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, <Link className="redirectLink" to="/login">–≤–æ–π–¥–∏—Ç–µ</Link> –∏–ª–∏{' '}
            <Link className="redirectLink" to="/register">–∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å</Link>.
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header
        title="–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∞–∫–∫–∞—É–Ω—Ç–∞–º–∏"
        description="–°–ø–∏—Å–æ–∫ –≤—Å–µ—Ö –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π"
      />

      {loading ? (
        <div className="welcome">–ó–∞–≥—Ä—É–∑–∫–∞ –∞–∫–∫–∞—É–Ω—Ç–æ–≤...</div>
      ) : error ? (
        <div className="formError">–û—à–∏–±–∫–∞: {error}</div>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>–õ–æ–≥–∏–Ω</th>
              <th>–†–æ–ª—å</th>
              <th>–ü–∞—Ä–æ–ª—å (–Ω–æ–≤—ã–π)</th>
              <th>–î–µ–π—Å—Ç–≤–∏—è</th>
            </tr>
          </thead>
          <tbody>
            {accounts.map((acc) => (
              <tr key={acc.id_account}>
                <td>{acc.id_account}</td>

                <td>
                  {editingId === acc.id_account ? (
                    <input
                      value={editData.login}
                      onChange={(e) => setEditData({ ...editData, login: e.target.value })}
                    />
                  ) : (
                    acc.login
                  )}
                </td>

                <td>
                  {editingId === acc.id_account ? (
                    <select
                      value={editData.role_id}
                      onChange={(e) => setEditData({ ...editData, role_id: e.target.value })}
                    >
                      <option value="">–í—ã–±–µ—Ä–∏ —Ä–æ–ª—å</option>
                      {roles.map((r) => (
                        <option key={r.id_role} value={r.id_role}>
                          {r.rolename}
                        </option>
                      ))}
                    </select>
                  ) : (
                    acc.rolename
                  )}
                </td>

                <td>
                  {editingId === acc.id_account ? (
                    <input
                      type="password"
                      placeholder="–ù–æ–≤—ã–π –ø–∞—Ä–æ–ª—å"
                      value={editData.password}
                      onChange={(e) => setEditData({ ...editData, password: e.target.value })}
                    />
                  ) : (
                    '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢'
                  )}
                </td>

                <td>
                  {editingId === acc.id_account ? (
                    <>
                      <button className="menuItem" onClick={() => handleSave(acc.id_account)}>
                        üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
                      </button>
                      <button
                        className="reviewDeleteBtn"
                        onClick={() => setEditingId(null)}
                      >
                        ‚úï
                      </button>
                    </>
                  ) : (
                    <>
                      <button className="menuItem" onClick={() => handleEdit(acc)}>
                        ‚úé
                      </button>
                      <button
                        className="reviewDeleteBtn"
                        onClick={() => handleDelete(acc.id_account)}
                        disabled={deletingId === acc.id_account}
                      >
                        {deletingId === acc.id_account ? '‚Ä¶' : '‚úï'}
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <Footer />
    </div>
  );
};

export default Accounts;
