import React, { useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Header from '../components/Header.js';

const Account = () =>
{
  const navigate = useNavigate();

  const storedUser = useMemo(() => {
    try {
      const userString = localStorage.getItem('user');
      return userString ? JSON.parse(userString) : null;
    } catch (e) {
      return null;
    }
  }, []);

  if (!storedUser)
  {
    return (
      <div>
        <Header title={'Учетная запись'} description={'Вы не вошли в систему'} />
        <div className="formContainer">
          <div className="hint">
            Пожалуйста, <Link className='redirectLink' to="/login">войдите</Link> или <Link className='redirectLink' to="/register">зарегистрируйтесь</Link>.
          </div>
        </div>
      </div>
    );
  }

  const fullName = [storedUser.firstname, storedUser.lastname].filter(Boolean).join(' ');

  return (
    <div>
      <Header title={'Учетная запись'} description={'Данные вашего профиля'} />
      <table class="table">
        <thead>
        </thead>
        <tbody>
          <tr>
            <th scope="row">Логин</th>
            <td>John</td>
            <td>Doe</td>
          </tr>
          <tr>
            <th scope="row">Пароль</th>
            <td>John</td>
            <td>Doe</td>
          </tr>
          <tr>
            <th scope="row">Эл. почта</th>
            <td>John</td>
            <td>Doe</td>
          </tr>
          <tr>
            <th scope="row">Фамилия</th>
            <td>Mark</td>
            <td>Otto</td>
          </tr>
          <tr>
            <th scope="row">Имя</th>
            <td>Jacob</td>
            <td>Thornton</td>
          </tr>
          <tr>
            <th scope="row">Отчество</th>
            <td>John</td>
            <td>Doe</td>
          </tr>
        </tbody>
      </table>

    </div>
  );
}

export default Account;
