import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const navigate = useNavigate();

  const [activeTab, setActiveTab] = useState("books");
  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [publishers, setPublishers] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  const [editingId, setEditingId] = useState(null);
  const [editedItem, setEditedItem] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [booksRes, authorsRes, publishersRes, categoriesRes] =
          await Promise.all([
            api.get("/books"),
            api.get("/authors"),
            api.get("/publishers"),
            api.get("/categories"),
          ]);
        setBooks(booksRes.data);
        setAuthors(authorsRes.data);
        setPublishers(publishersRes.data);
        setCategories(categoriesRes.data);

      } catch (err) {
        console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð´Ð°Ð½Ð½Ñ‹Ñ…:", err);
        alert("ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleEditClick = (item) => {
    setEditingId(
      item.id_book || item.id_author || item.id_publisher || item.id_category
    );

    setEditedItem({
      id_book: item.id_book,
      title: item.title,
      price: item.price,
      quantity: item.quantity,
      description: item.description || "",
      imageurl: item.imageurl || "",
      author_id: item.author_id,
      category_id: item.category_id,
      publisher_id: item.publisher_id, // ðŸ‘ˆ Ð²Ð°Ð¶Ð½Ð¾
    });
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditedItem({});
  };

  const handleInputChange = (field, value) => {
    setEditedItem((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleKeyDown = async (e, type) => {
    if (e.key === "Enter") {
      e.preventDefault();
      await handleSaveChanges(type);
    }
  };

  const handleSaveChanges = async (type) => {
    try {
      let endpoint = "";
      let data = {};

      switch (type) {
        case "books":
          endpoint = `/books/${editedItem.id_book}`;
          data = {
            title: editedItem.title,
            price: editedItem.price,
            quantity: editedItem.quantity,
            description: editedItem.description || "",
            author_id: editedItem.author_id,
            category_id: editedItem.category_id,
            publisher_id: editedItem.publisher_id,
            imageurl: editedItem.imageurl,
          };

          await api.put(endpoint, data);

          const publisherName =
            publishers.find(
              (p) => p.id_publisher === Number(editedItem.publisher_id)
            )?.legalname || "â€”";

          setBooks((prev) =>
            prev.map((b) =>
              b.id_book === editedItem.id_book
                ? {
                    ...b,
                    ...editedItem,
                    publisher_name: publisherName, 
                  }
                : b
            )
          );
          break;

        case "authors":
          endpoint = `/authors/${editedItem.id_author}`;
          data = {
            firstname: editedItem.firstname,
            lastname: editedItem.lastname,
            patronymic: editedItem.patronymic,
            birthdate: editedItem.birthdate,
            deathdate: editedItem.deathdate,
          };
          await api.put(endpoint, data);
          setAuthors((prev) =>
            prev.map((a) =>
              a.id_author === editedItem.id_author ? { ...a, ...editedItem } : a
            )
          );
          break;

        case "publishers":
          endpoint = `/publishers/${editedItem.id_publisher}`;
          data = {
            legalname: editedItem.legalname,
            contactnum: editedItem.contactnum,
            email: editedItem.email,
          };
          await api.put(endpoint, data);
          setPublishers((prev) =>
            prev.map((p) =>
              p.id_publisher === editedItem.id_publisher ? { ...p, ...editedItem } : p
            )
          );
          break;

        case "categories":
          endpoint = `/categories/${editedItem.id_category}`;
          data = { name: editedItem.name };
          await api.put(endpoint, data);
          setCategories((prev) =>
            prev.map((c) =>
              c.id_category === editedItem.id_category ? { ...c, ...editedItem } : c
            )
          );
          break;

        default:
          break;
      }

      alert("Ð˜Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ñ‹.");
      handleCancelEdit();
    } catch (err) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ð¸:", err);
      alert("ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ.");
    }
  };

  const handleDelete = async (endpoint, id, stateSetter) => {
    if (!window.confirm("Ð’Ñ‹ ÑƒÐ²ÐµÑ€ÐµÐ½Ñ‹, Ñ‡Ñ‚Ð¾ Ñ…Ð¾Ñ‚Ð¸Ñ‚Ðµ ÑƒÐ´Ð°Ð»Ð¸Ñ‚ÑŒ Ð·Ð°Ð¿Ð¸ÑÑŒ?")) return;
    try {
      await api.delete(`/${endpoint}/${id}`);
      stateSetter((prev) =>
        prev.filter(
          (item) =>
            item.id_book !== id &&
            item.id_author !== id &&
            item.id_publisher !== id &&
            item.id_category !== id
        )
      );
      alert("Ð£Ð´Ð°Ð»ÐµÐ½Ð¾ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾.");
    } catch (err) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ð¸:", err);
      alert("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ð¸.");
    }
  };

  if (loading) {
    return (
      <div>
        <Header title="Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°..." description="ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð¿Ð¾Ð´Ð¾Ð¶Ð´Ð¸Ñ‚Ðµ" />
      </div>
    );
  }

  return (
    <div>
      <Header
        title="Ð£Ð¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ñ‚Ð¾Ð²Ð°Ñ€Ð°Ð¼Ð¸"
        description="Ð—Ð´ÐµÑÑŒ Ð²Ñ‹ Ð¼Ð¾Ð¶ÐµÑ‚Ðµ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÑÑ‚ÑŒ Ñ‚Ð¾Ð²Ð°Ñ€Ð°Ð¼Ð¸"
      />

      {/* ÐŸÐµÑ€ÐµÐºÐ»ÑŽÑ‡Ð°Ñ‚ÐµÐ»ÑŒ */}
      <div className="formContainer tabButtons">
        <button
          className={`menuItem ${activeTab === "books" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("books")}
        >
          ÐšÐ½Ð¸Ð³Ð¸
        </button>
        <button
          className={`menuItem ${activeTab === "authors" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("authors")}
        >
          ÐÐ²Ñ‚Ð¾Ñ€Ñ‹
        </button>
        <button
          className={`menuItem ${activeTab === "publishers" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("publishers")}
        >
          Ð˜Ð·Ð´Ð°Ñ‚ÐµÐ»Ð¸
        </button>
        <button
          className={`menuItem ${activeTab === "categories" ? "activeTab" : ""}`}
          onClick={() => setActiveTab("categories")}
        >
          ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸
        </button>
      </div>

      {/* ÐšÐ½Ð¾Ð¿ÐºÐ¸ Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ñ */}
      <div className="formContainer">
        {activeTab === "books" && (
          <button
            className="menuItem"
            onClick={() => navigate("/products/add-book")}
          >
            âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÐºÐ½Ð¸Ð³Ñƒ
          </button>
        )}
        {activeTab === "authors" && (
          <button
            className="menuItem"
            onClick={() => navigate("/products/add-author")}
          >
            âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð°Ð²Ñ‚Ð¾Ñ€Ð°
          </button>
        )}
        {activeTab === "publishers" && (
          <button
            className="menuItem"
            onClick={() => navigate("/products/add-publisher")}
          >
            âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð¸Ð·Ð´Ð°Ñ‚ÐµÐ»Ñ
          </button>
        )}
        {activeTab === "categories" && (
          <button
            className="menuItem"
            onClick={() => navigate("/products/add-category")}
          >
            âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸ÑŽ
          </button>
        )}
      </div>

      <div className="tableContainer">
        {/* ÐšÐ½Ð¸Ð³Ð¸ */}
        {activeTab === "books" && (
          <>
            <h2 className="head">ðŸ“š Ð’ÑÐµ ÐºÐ½Ð¸Ð³Ð¸ ðŸ“š</h2>
            {books.length === 0 ? (
              <p className="hint">ÐšÐ½Ð¸Ð³Ð¸ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½Ñ‹.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>ÐžÐ±Ð»Ð¾Ð¶ÐºÐ°</th>
                    <th>ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ</th>
                    <th>ÐÐ²Ñ‚Ð¾Ñ€</th>
                    <th>ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ</th>
                    <th>Ð˜Ð·Ð´Ð°Ñ‚ÐµÐ»ÑŒ</th>
                    <th>Ð¦ÐµÐ½Ð°</th>
                    <th>ÐšÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾</th>
                    <th>Ð”ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ</th>
                  </tr>
                </thead>
                <tbody>
                  {books.map((b) =>
                    editingId === b.id_book ? (
                      <tr key={b.id_book}>
                        <td>{b.id_book}</td>

                        <td>
                          <input
                            className="formInput"
                            type="text"
                            placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÑÑÑ‹Ð»ÐºÑƒ Ð½Ð° Ð¸Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ"
                            value={editedItem.imageurl || ""}
                            onChange={(e) => handleInputChange("imageurl", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                            autoFocus
                          />
                        </td>

                        <td>
                          <input
                            className="formInput"
                            value={editedItem.title || ""}
                            onChange={(e) => handleInputChange("title", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          />
                        </td>

                        <td>
                          <select
                            className="formInput"
                            value={editedItem.author_id || ""}
                            onChange={(e) => handleInputChange("author_id", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          >
                            {authors.map((a) => (
                              <option key={a.id_author} value={a.id_author}>
                                {a.firstname} {a.lastname}
                              </option>
                            ))}
                          </select>
                        </td>

                        <td>
                          <select
                            className="formInput"
                            value={editedItem.category_id || ""}
                            onChange={(e) => handleInputChange("category_id", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          >
                            {categories.map((c) => (
                              <option key={c.id_category} value={c.id_category}>
                                {c.name}
                              </option>
                            ))}
                          </select>
                        </td>

                        <td>
                          <select
                            className="formInput"
                            value={editedItem.publisher_id || ""}
                            onChange={(e) => handleInputChange("publisher_id", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          >
                            {publishers.map((p) => (
                              <option key={p.id_publisher} value={p.id_publisher}>
                                {p.legalname}
                              </option>
                            ))}
                          </select>
                        </td>

                        <td>
                          <input
                            className="formInput"
                            type="number"
                            value={editedItem.price || 0}
                            onChange={(e) => handleInputChange("price", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          />
                        </td>

                        <td>
                          <input
                            className="formInput"
                            type="number"
                            value={editedItem.quantity || 0}
                            onChange={(e) => handleInputChange("quantity", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "books")}
                          />
                        </td>

                        <td>
                          <button className="menuItem" onClick={() => handleSaveChanges("books")}>
                            Ð¡Ð¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ
                          </button>
                          <button className="menuItem" onClick={handleCancelEdit}>
                            ÐžÑ‚Ð¼ÐµÐ½Ð°
                          </button>
                        </td>
                      </tr>
                    ) : (
                      <tr key={b.id_book}>
                        <td>{b.id_book}</td>
                        <td>
                          {b.imageurl ? (
                            <img src={b.imageurl} alt={b.title} className="bookImage" />
                          ) : (
                            <div className="noImage">ÐÐµÑ‚ Ñ„Ð¾Ñ‚Ð¾</div>
                          )}
                        </td>
                        <td>{b.title}</td>
                        <td>{`${b.author_firstname || ""} ${b.author_lastname || ""}`}</td>
                        <td>{b.category_name}</td>
                        <td>
                          {publishers.find((p) => p.id_publisher === b.publisher_id)?.legalname || "â€”"}
                        </td>
                        <td>{b.price} â‚½</td>
                        <td>{b.quantity}</td>
                        <td>
                          <button className="menuItem" onClick={() => handleEditClick(b)}>
                            Ð˜Ð·Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ
                          </button>
                          <button
                            className="menuItemDanger"
                            onClick={() => handleDelete("books", b.id_book, setBooks)}
                          >
                            Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ
                          </button>
                        </td>
                      </tr>
                    )
                  )}
                </tbody>
              </table>

            )}
          </>
        )}

        {/* ÐÐ²Ñ‚Ð¾Ñ€Ñ‹ */}
        {activeTab === "authors" && (
          <>
            <h2 className="head">âœï¸ Ð’ÑÐµ Ð°Ð²Ñ‚Ð¾Ñ€Ñ‹ âœï¸</h2>
            <button className="menuItem" onClick={() => navigate("/products/add-author")}>
              âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð°Ð²Ñ‚Ð¾Ñ€Ð°
            </button>
            {authors.length === 0 ? (
              <p className="hint">ÐÐ²Ñ‚Ð¾Ñ€Ñ‹ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½Ñ‹.</p>
            ) : (
              <table className="table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Ð¤Ð°Ð¼Ð¸Ð»Ð¸Ñ</th>
                    <th>Ð˜Ð¼Ñ</th>
                    <th>ÐžÑ‚Ñ‡ÐµÑÑ‚Ð²Ð¾</th>
                    <th>Ð”Ð°Ñ‚Ð° Ñ€Ð¾Ð¶Ð´ÐµÐ½Ð¸Ñ</th>
                    <th>Ð”Ð°Ñ‚Ð° ÑÐ¼ÐµÑ€Ñ‚Ð¸</th>
                    <th>Ð”ÐµÐ¹ÑÑ‚Ð²Ð¸Ñ</th>
                  </tr>
                </thead>
                <tbody>
                  {authors.map((a) =>
                    editingId === a.id_author ? (
                      <tr key={a.id_author}>
                        <td>{a.id_author}</td>
                        <td>
                          <input
                            className="formInput"
                            value={editedItem.lastname || ""}
                            onChange={(e) => handleInputChange("lastname", e.target.value) }
                            onKeyDown={(e) => handleKeyDown(e, "authors")}
                          />
                        </td>
                        <td>
                          <input
                            className="formInput"
                            value={editedItem.firstname || ""}
                            onChange={(e) => handleInputChange("firstname", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "authors")}
                          />
                        </td>
                        <td>
                          <input
                            className="formInput"
                            value={editedItem.patronymic || ""}
                            onChange={(e) => handleInputChange("patronymic", e.target.value) }
                            onKeyDown={(e) => handleKeyDown(e, "authors")}
                          />
                        </td>
                        <td>
                          <input
                            className="formInput"
                            type="date"
                            value={editedItem.birthdate?.slice(0, 10) || ""}
                            onChange={(e) => handleInputChange("birthdate", e.target.value) }
                            onKeyDown={(e) => handleKeyDown(e, "authors")}
                          />
                        </td>
                        <td>
                          <input
                            className="formInput"
                            type="date"
                            value={editedItem.deathdate?.slice(0, 10) || ""}
                            onChange={(e) => handleInputChange("deathdate", e.target.value)}
                            onKeyDown={(e) => handleKeyDown(e, "authors")}
                          />
                        </td>
                        <td>
                          <button
                            className="menuItem"
                            onClick={() => handleSaveChanges("authors")}
                          >
                            Ð¡Ð¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ
                          </button>
                          <button className="menuItem" onClick={handleCancelEdit}>
                            ÐžÑ‚Ð¼ÐµÐ½Ð°
                          </button>
                        </td>
                      </tr>
                    ) : (
                      <tr key={a.id_author}>
                        <td>{a.id_author}</td>
                        <td>{a.lastname}</td>
                        <td>{a.firstname}</td>
                        <td>{a.patronymic || "â€”"}</td>
                        <td>
                          {a.birthdate
                            ? new Date(a.birthdate).toLocaleDateString("ru-RU")
                            : "â€”"}
                        </td>
                        <td>
                          {a.deathdate
                            ? new Date(a.deathdate).toLocaleDateString("ru-RU")
                            : "â€”"}
                        </td>
                        <td>
                          <button className="menuItem" onClick={() => handleEditClick(a)}>
                            Ð˜Ð·Ð¼ÐµÐ½Ð¸Ñ‚ÑŒ
                          </button>
                          <button
                            className="menuItemDanger"
                            onClick={() => handleDelete("authors", a.id_author, setAuthors)}
                          >
                            Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ
                          </button>
                        </td>
                      </tr>
                    )
                  )}
                </tbody>
              </table>
