import React, { useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Header from '../components/Header.js';

const Account = () =>
{
  const navigate = useNavigate();

  const storedUser = useMemo(() => {
    try {
      const userString = localStorage.getItem('user');
      return userString ? JSON.parse(userString) : null;
    } catch (e) {
      return null;
    }
  }, []);

  if (!storedUser)
  {
    return (
      <div>
        <Header title={'Учетная запись'} description={'Вы не вошли в систему'} />
        <div className="formContainer">
          <div className="hint">
            Пожалуйста, <Link className='redirectLink' to="/login">войдите</Link> или <Link className='redirectLink' to="/register">зарегистрируйтесь</Link>.
          </div>
        </div>
      </div>
    );
  }

  const fullName = [storedUser.firstname, storedUser.lastname].filter(Boolean).join(' ');

  return (
    <div>
      <Header title={'Учетная запись'} description={'Ваши данные профиля'} />
      <div className="profileContainer">
        <div className="profileCard">
          <h2 className="profileTitle">Профиль</h2>

          <table className="profileTable">
            <tbody>
              <tr>
                <td className="profileLabel">Логин:</td>
                <td className="profileValue">{storedUser.login || '—'}</td>
              </tr>
              <tr>
                <td className="profileLabel">Имя:</td>
                <td className="profileValue">{fullName || '—'}</td>
              </tr>
              <tr>
                <td className="profileLabel">Email:</td>
                <td className="profileValue">{storedUser.email || '—'}</td>
              </tr>
              <tr>
                <td className="profileLabel">Роль:</td>
                <td className="profileValue">{storedUser.rolename || storedUser.role || storedUser.role_id || '—'}</td>
              </tr>
            </tbody>
          </table>

          <div className="accountActions">
            <button className="actionButton" onClick={() => navigate('/orders')}>Мои заказы</button>
            <button className="actionButton" onClick={() => navigate('/settings')}>Настройки</button>
          </div>
        </div>
    </div>
  </div>

  );
}

export default Account;
