import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header.js';
import Footer from '../components/Footer.js';
import Card from '../components/CardItem.js'
import axios from 'axios';

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/orders/${id}`);
        const data = res.data;

        console.log(data);
        console.log(data.items);
        setOrder(data);
      } catch (err) {
        console.error('Ошибка при получении деталей заказа:', err);
        setError('Не удалось загрузить данные заказа.');
      }
    };

    fetchOrderDetails();
  }, [id]);

  if (error) return <div className="welcome">{error}</div>;
  if (!order) return <div className="welcome">Загрузка...</div>;

  return (
    <div>
      <Header title={`Заказ №${order.id_order}`} description="Детали заказа" />

      <div className="orderDetails">
        <h3 className="welcome">Дата: {new Date(order.orderdate).toLocaleDateString()}</h3>
        <h3 className="welcome">
          Статус: {order.status }
        </h3>
        <h3 className="welcome">Сумма: {order.totalprice} ₽</h3>
        <h3 className="welcome">
          Тип доставки: {order.typename}
        </h3>

        {order.address_id ? (
          <h3 className="welcome">
            Адрес доставки: {order.indexmail}, {order.country}, г. {order.city}, ул. {order.street}, д. {order.house}, кв. {order.apartment}</h3>
        ) : ('')}

        <h3 className="welcome">Товары:</h3>
          <div className="orderCards">
            {order.items && order.items.length > 0 ? (
              order.items.map((item, i) => (
                <Card
                  key={i}
                  item={{
                    id: item.id_book,
                    name: item.title,
                    author: item.author,
                    url: item.image_url,
                    price: item.price,
                    addedAmount: item.quantity,
                    isFavorite: false, 
                  }}
                  becomeFavorite={() => {}}
                  addToCart={() => {}}
                  removeFromCart={() => {}}
                  changePrice={false}
                />
              ))
            ) : (
              <h3 className="find">Нет информации о товарах</h3>
            )}
          </div>

      </div>

      <div className="formContainer">
        <Link className="menuItem" to="/orders">← Назад к заказам</Link>
      </div>

      <Footer />
    </div>
  );
};

export default OrderDetails;
