import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header.js';
import Footer from '../components/Footer.js';
import api from '../axiosSetup.js';
import { getCurrentUser } from '../utils/userUtils.js';

const AuditLogManager = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedLog, setSelectedLog] = useState(null);

  const [filters, setFilters] = useState({
    table: '',
    action: '',
    user: ''
  });

  // загрузка журнала аудита
  const loadLogs = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (filters.table) params.append('table', filters.table);
      if (filters.action) params.append('action', filters.action);
      if (filters.user) params.append('user', filters.user);

      const res = await api.get(`/audit?${params.toString()}`);
      setLogs(res.data);
    } catch (err) {
      console.error('Ошибка при загрузке журнала аудита:', err);
    } finally {
      setLoading(false);
    }
  };

  // загрузка конкретной записи
  const loadLogDetails = async (id) => {
    try {
      const res = await api.get(`/audit/${id}`);
      setSelectedLog(res.data);
    } catch (err) {
      console.error('Ошибка при получении деталей записи аудита:', err);
    }
  };

  useEffect(() => {
    loadLogs();
  }, []);

  // фильтрация
  const handleFilterChange = (field, value) => {
    setFilters((prev) => ({ ...prev, [field]: value }));
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    loadLogs();
  };

  if (!getCurrentUser()) {
    return (
      <div>
        <Header title="Журнал аудита" description="Вы не вошли в систему" />
        <div className="formContainer">
          <div className="hint">
            Пожалуйста, <Link className="redirectLink" to="/login">войдите</Link>.
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header title="Журнал аудита" description="Просмотр изменений в базе данных" />

      <div className="formContainer">
        <form className="filterForm" onSubmit={handleFilterSubmit}>
          <input
            type="text"
            placeholder="Таблица..."
            className="formInput"
            value={filters.table}
            onChange={(e) => handleFilterChange('table', e.target.value)}
          />
          <select
            className="formInput"
            value={filters.action}
            onChange={(e) => handleFilterChange('action', e.target.value)}
          >
            <option value="">Все действия</option>
            <option value="INSERT">INSERT</option>
            <option value="UPDATE">UPDATE</option>
            <option value="DELETE">DELETE</option>
          </select>
          <input
            type="text"
            placeholder="Пользователь..."
            className="formInput"
            value={filters.user}
            onChange={(e) => handleFilterChange('user', e.target.value)}
          />
          <button type="submit" className="menuItem">Фильтр</button>
        </form>
      </div>

      {loading ? (
        <div className="loader">Загрузка...</div>
      ) : (
        <div className="tableContainer">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Таблица</th>
                <th>ID записи</th>
                <th>Действие</th>
                <th>Пользователь</th>
                <th>Дата</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log) => (
                <tr
                  key={log.id_audit}
                  onClick={() => loadLogDetails(log.id_audit)}
                  className={selectedLog?.id_audit === log.id_audit ? 'selectedRow' : ''}
                >
                  <td>{log.id_audit}</td>
                  <td>{log.tablename}</td>
                  <td>{log.record_id ?? '-'}</td>
                  <td>{log.action}</td>
                  <td>{log.changedby || '-'}</td>
                  <td>{new Date(log.changedat).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {selectedLog && (
        <div className="detailsContainer">
          <h3>Детали изменения (ID {selectedLog.id_audit})</h3>
          <p><strong>Таблица:</strong> {selectedLog.tablename}</p>
          <p><strong>Пользователь:</strong> {selectedLog.changedby}</p>
          <p><strong>Действие:</strong> {selectedLog.action}</p>
          <p><strong>Дата:</strong> {new Date(selectedLog.changedat).toLocaleString()}</p>

          <div className="jsonBlock">
            <h4>Старое значение:</h4>
            <pre>{JSON.stringify(selectedLog.oldvalue, null, 2) || '-'}</pre>
          </div>

          <div className="jsonBlock">
            <h4>Новое значение:</h4>
            <pre>{JSON.stringify(selectedLog.newvalue, null, 2) || '-'}</pre>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
};

export default AuditLogManager;
