import React, { useState, useEffect } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [book, setBook] = useState(null);
  const [editingField, setEditingField] = useState(null);

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("");

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await api.get(`/books/${id}`);
        const data = response.data;
        setBook(data);
        setTitle(data.title);
        setDescription(data.description);
        setPrice(data.price);
        setQuantity(data.quantity);
      } catch (err) {
        console.error("Ошибка при загрузке книги:", err);
        alert("Не удалось загрузить книгу");
      }
    };

    fetchBook();
  }, [id]);

  const handleEditClick = (field) => {
    setEditingField(field);
  };

  const saveChanges = async () => {
    try {
      const response = await api.put(`/books/${id}`, {
        title,
        description,
        price,
        quantity,
      });

      setBook(response.data);
      console.log("Книга обновлена:", response.data);
    } catch (err) {
      console.error("Ошибка при сохранении книги:", err);
      alert("Ошибка при сохранении книги.");
    }
  };

  const handleBlur = async () => {
    await saveChanges();
    setEditingField(null);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.target.blur();
    }
  };

  const handleDeleteBook = async () => {
    if (!window.confirm("Вы уверены, что хотите удалить эту книгу?")) return;
    try {
      await api.delete(`/books/${id}`);
      alert("Книга удалена.");
      navigate("/books");
    } catch (err) {
      console.error("Ошибка при удалении книги:", err);
      alert("Ошибка при удалении книги.");
    }
  };

  if (!book) {
    return (
      <div>
        <Header title="Изменение книги" description="Загрузка данных..." />
      </div>
    );
  }

  return (
    <div>
      <Header title="Изменение книги" description={`Редактирование: ${book.title}`} />
      <table className="table">
        <tbody>
          <tr>
            <th scope="row">Название</th>
            <td>
              {editingField === "title" ? (
                <input
                  className="formInput"
                  type="text"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  onBlur={handleBlur}
                  onKeyDown={handleKeyDown}
                  autoFocus
                />
              ) : (
                title
              )}
            </td>
            <td>
              <button
                className="menuItem"
                onClick={() => handleEditClick("title")}
              >
                Изменить
              </button>
            </td>
          </tr>

          <tr>
            <th scope="row">Описание</th>
            <td>
              {editingField === "description" ? (
                <textarea
                  className="formInput"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  onBlur={handleBlur}
                  onKeyDown={handleKeyDown}
                  autoFocus
                />
              ) : (
                description
              )}
            </td>
            <td>
              <button
                className="menuItem"
                onClick={() => handleEditClick("description")}
              >
                Изменить
              </button>
            </td>
          </tr>

          <tr>
            <th scope="row">Цена</th>
            <td>
              {editingField === "price" ? (
                <input
                  className="formInput"
                  type="number"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                  onBlur={handleBlur}
                  onKeyDown={handleKeyDown}
                  autoFocus
                />
              ) : (
                `${price} ₽`
              )}
            </td>
            <td>
              <button
                className="menuItem"
                onClick={() => handleEditClick("price")}
              >
                Изменить
              </button>
            </td>
          </tr>

          <tr>
            <th scope="row">Количество</th>
            <td>
              {editingField === "quantity" ? (
                <input
                  className="formInput"
                  type="number"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  onBlur={handleBlur}
                  onKeyDown={handleKeyDown}
                  autoFocus
                />
              ) : (
                quantity
              )}
            </td>
            <td>
              <button
                className="menuItem"
                onClick={() => handleEditClick("quantity")}
              >
                Изменить
              </button>
            </td>
          </tr>

          <tr>
            <th scope="row">Автор</th>
            <td>{book.author_firstname} {book.author_lastname}</td>
            <td></td>
          </tr>

          <tr>
            <th scope="row">Категория</th>
            <td>{book.category_name}</td>
            <td></td>
          </tr>
        </tbody>
      </table>

      <div className="formContainer">
        <button className="menuItemDanger" onClick={handleDeleteBook}>
          Удалить книгу
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default Products;
