import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header.js';
import Footer from '../components/Footer.js';
import api from '../axiosSetup.js';
import { getCurrentUser } from '../utils/userUtils.js';
import AuditManager from '../components/AuditManager.js'; // üëà –¥–æ–±–∞–≤—å –∏–º–ø–æ—Ä—Ç

const AuditLogManager = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedLog, setSelectedLog] = useState(null);

  const [filters, setFilters] = useState({
    table: '',
    action: '',
    user: ''
  });

  const loadLogs = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (filters.table) params.append('table', filters.table);
      if (filters.action) params.append('action', filters.action);
      if (filters.user) params.append('user', filters.user);

      const res = await api.get(`/audit?${params.toString()}`);
      setLogs(res.data);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∂—É—Ä–Ω–∞–ª–∞ –∞—É–¥–∏—Ç–∞:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadLogDetails = async (id) => {
    try {
      const res = await api.get(`/audit/${id}`);
      setSelectedLog(res.data);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–µ—Ç–∞–ª–µ–π –∑–∞–ø–∏—Å–∏ –∞—É–¥–∏—Ç–∞:', err);
    }
  };

  useEffect(() => {
    loadLogs();
  }, []);

  const handleFilterChange = (field, value) => {
    setFilters((prev) => ({ ...prev, [field]: value }));
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    loadLogs();
  };

  if (!getCurrentUser()) {
    return (
      <div>
        <Header title="–ñ—É—Ä–Ω–∞–ª –∞—É–¥–∏—Ç–∞" description="–í—ã –Ω–µ –≤–æ—à–ª–∏ –≤ —Å–∏—Å—Ç–µ–º—É" />
        <div className="formContainer">
          <div className="hint">
            –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, <Link className="redirectLink" to="/login">–≤–æ–π–¥–∏—Ç–µ</Link>.
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Header title="–ñ—É—Ä–Ω–∞–ª –∞—É–¥–∏—Ç–∞" description="–ü—Ä–æ—Å–º–æ—Ç—Ä –∏–∑–º–µ–Ω–µ–Ω–∏–π –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö" />

      <div className="formContainer">
        <form className="filterForm" onSubmit={handleFilterSubmit}>
          <input
            type="text"
            placeholder="–¢–∞–±–ª–∏—Ü–∞..."
            className="formInput"
            value={filters.table}
            onChange={(e) => handleFilterChange('table', e.target.value)}
          />
          <select
            className="formInput"
            value={filters.action}
            onChange={(e) => handleFilterChange('action', e.target.value)}
          >
            <option value="">–í—Å–µ –¥–µ–π—Å—Ç–≤–∏—è</option>
            <option value="INSERT">INSERT</option>
            <option value="UPDATE">UPDATE</option>
            <option value="DELETE">DELETE</option>
          </select>
          <input
            type="text"
            placeholder="–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å..."
            className="formInput"
            value={filters.user}
            onChange={(e) => handleFilterChange('user', e.target.value)}
          />
          <button type="submit" className="menuItem">–§–∏–ª—å—Ç—Ä</button>
        </form>
      </div>

      {loading ? (
        <div className="loader">–ó–∞–≥—Ä—É–∑–∫–∞...</div>
      ) : (
        <div className="tableContainer">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>–¢–∞–±–ª–∏—Ü–∞</th>
                <th>ID –∑–∞–ø–∏—Å–∏</th>
                <th>–î–µ–π—Å—Ç–≤–∏–µ</th>
                <th>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å</th>
                <th>–î–∞—Ç–∞</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log) => (
                <tr
                  key={log.id_audit}
                  onClick={() => loadLogDetails(log.id_audit)}
                  className={selectedLog?.id_audit === log.id_audit ? 'selectedRow' : ''}
                >
                  <td>{log.id_audit}</td>
                  <td>{log.tablename}</td>
                  <td>{log.record_id ?? '-'}</td>
                  <td>{log.action}</td>
                  <td>{log.changedby || '-'}</td>
                  <td>{new Date(log.changedat).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* ‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º –æ—Ç–¥–µ–ª—å–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç */}
      {selectedLog && <AuditManager log={selectedLog} />}

      <Footer />
    </div>
  );
};

export default AuditLogManager;
