import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCurrentUser } from '../utils/userUtils.js';
import { getCartFromStorage } from '../utils/cartUtils.js';

const Header = ({ title, description }) => {
  const navigate = useNavigate();
  const menuRef = useRef(null);
  const user = getCurrentUser();

  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isCartNotEmpty, setIsCartNotEmpty] = useState(getCartFromStorage().length > 0);

  const handleButtonClick = () => navigate('/cart');
  const toggleMenu = () => setIsMenuOpen(prev => !prev);

  const handleNavigateAccount = () => {
    setIsMenuOpen(false);
    navigate('/account');
  };

  const handleNavigateReports = () => {
    setIsMenuOpen(false);
    navigate('/reports');
  };

  const handleNavigateProducts = () => {
    setIsMenuOpen(false);
    navigate('/products');
  };

  const handleNavigateOrders = () => {
    setIsMenuOpen(false);
    navigate('/orders');
  };

  const handleNavigateOrdersManage = () => {
    setIsMenuOpen(false);
    navigate('/orders-manager');
  };

  const handleNavigateAudit = () => {
    setIsMenuOpen(false);
    navigate('/audit');
  };

  const handleLogout = () => {
    setIsMenuOpen(false);
    try {
      localStorage.removeItem('user');
    } catch (e) {}
    navigate('/');
  };

  useEffect(() => {
    const handleStorageChange = () => {
      const cart = getCartFromStorage();
      setIsCartNotEmpty(cart.length > 0);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // –∑–∞–∫—Ä—ã—Ç–∏–µ –º–µ–Ω—é –ø—Ä–∏ –∫–ª–∏–∫–µ –≤–Ω–µ –µ–≥–æ
  useEffect(() => {
    const handleClickOutside = event => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  useEffect(() => {
    const handleKeyDown = e => {
      if (!user) return;
      if (e.altKey) {
        switch (e.key.toLowerCase()) {
          case 'm': // –º–µ–Ω—é
            e.preventDefault();
            setIsMenuOpen(prev => !prev);
            break;
          case 'c': // –∫–æ—Ä–∑–∏–Ω–∞
            e.preventDefault();
            handleButtonClick();
            break;
          case 'o': // –æ—Ç—á—ë—Ç—ã –∏–ª–∏ –∑–∞–∫–∞–∑—ã
            e.preventDefault();
            user.role_id === 1 ? handleNavigateReports() : handleNavigateOrders();
            break;
          case 'p': // —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏ 
            if (user.role_id === 1) {
              e.preventDefault();
              handleNavigateProducts();
            }
            break;
          case 'a': // –∞—É–¥–∏—Ç 
            if (user.role_id === 1) {
              e.preventDefault();
              handleNavigateAudit();
            }
            break;
          case 'q': // –≤—ã—Ö–æ–¥
            e.preventDefault();
            handleLogout();
            break;
          default:
            break;
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [user]);

  return (
    <div>
      <div className="mainpage">
        <div className="menuWrapper" ref={menuRef}>
          <button
            className="menuButton"
            onClick={toggleMenu}
            aria-haspopup="true"
            aria-expanded={isMenuOpen}
          >
            ‚öô
          </button>
          {isMenuOpen && (
            <div className="menuDropdown" role="menu">
              <button className="menuItem" onClick={handleNavigateAccount} role="menuitem">
                –£—á–µ—Ç–Ω–∞—è –∑–∞–ø–∏—Å—å
              </button>
              <button
                className="menuItem"
                onClick={user.role_id === 1 ? handleNavigateReports : handleNavigateOrders}
                role="menuitem"
              >
                {user.role_id === 1 ? '–û—Ç—á—ë—Ç—ã' : '–ó–∞–∫–∞–∑—ã'}
              </button>
              {user.role_id === 1 && (
                <button className="menuItem" onClick={handleNavigateProducts} role="menuitem">
                  –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏
                </button>
              )}
              {user.role_id === 1 && (
                <button className="menuItem" onClick={handleNavigateOrdersManage} role="menuitem">
                  –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∑–∞–∫–∞–∑–∞–º–∏
                </button>
              )}
              {user.role_id === 1 && (
                <button className="menuItem" onClick={handleNavigateAudit} role="menuitem">
                  –ñ—É—Ä–Ω–∞–ª –∞—É–¥–∏—Ç–∞
                </button>
              )}
              <button className="menuItem menuItemDanger" onClick={handleLogout} role="menuitem">
                –í—ã–π—Ç–∏
              </button>
            </div>
          )}
        </div>
        <header className="welcome">{title}</header>
        <button
          className={isCartNotEmpty ? 'cartFull' : 'cartEmpty'}
          onClick={handleButtonClick}
        >
          üõí
        </button>
      </div>
      <h2 className="find">{description}</h2>
    </div>
  );
};

export default Header;
