import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const navigate = useNavigate();

  const [books, setBooks] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingBookId, setEditingBookId] = useState(null);
  const [editedBook, setEditedBook] = useState({});
  const [editedImageUrl, setEditedImageUrl] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [booksRes, authorsRes, categoriesRes] = await Promise.all([
          api.get("/books"),
          api.get("/authors"),
          api.get("/categories"),
        ]);

        setBooks(booksRes.data);
        setAuthors(authorsRes.data);
        setCategories(categoriesRes.data);
      } catch (err) {
        console.error("Ошибка при загрузке данных:", err);
        alert("Не удалось загрузить книги, авторов или категории.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleEditClick = (book) => {
    setEditingBookId(book.id_book);
    setEditedBook({ ...book });
  };

  const handleInputChange = (field, value) => {
    setEditedBook((prev) => ({
      ...prev,
      [field]: value,
    }));
  };


  const handleSaveChanges = async () => {
    try {
      const formData = new FormData();
      formData.append("title", editedBook.title);
      formData.append("price", editedBook.price);
      formData.append("quantity", editedBook.quantity);
      formData.append("description", editedBook.description || "");
      formData.append("author_id", editedBook.author_id);
      formData.append("category_id", editedBook.category_id);
      if (editedBook.image) {
        formData.append("image", editedBook.image);
      }

      await api.put(`/books/${editingBookId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setBooks((prev) =>
        prev.map((b) =>
          b.id_book === editingBookId ? { ...b, ...editedBook } : b
        )
      );

      alert("Изменения сохранены.");
      setEditingBookId(null);
      setEditedBook({});
    } catch (err) {
      console.error("Ошибка при сохранении изменений:", err);
      alert("Ошибка при сохранении данных.");
    }
  };

  const handleDelete = async (id_book) => {
    if (!window.confirm("Вы уверены, что хотите удалить эту книгу?")) return;
    try {
      await api.delete(`/books/${id_book}`);
      setBooks((prev) => prev.filter((b) => b.id_book !== id_book));
      alert("Книга удалена.");
    } catch (err) {
      console.error("Ошибка при удалении книги:", err);
      alert("Ошибка при удалении книги.");
    }
  };

  const handleKeyDown = async (e) => {
    if (e.key === "Enter") {
      await handleSaveChanges();
    }
  };

  if (loading) {
    return (
      <div>
        <Header title="Книги" description="Загрузка данных..." />
      </div>
    );
  }

  return (
    <div>
      <Header title="Все книги" description="Список всех товаров" />

      <div className="formContainer">
        <button className="menuItem" onClick={() => navigate("/books/add")}>
          ➕ Добавить книгу
        </button>
      </div>

      {books.length === 0 ? (
        <p className="hint">Книги не найдены.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Обложка</th>
              <th>Название</th>
              <th>Автор</th>
              <th>Категория</th>
              <th>Цена</th>
              <th>Количество</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {books.map((b) => (
              <tr key={b.id_book}>
                <td>{b.id_book}</td>

                <td>
                {editingBookId === b.id_book ? (
                    <div>
                    <input
                        type="text"
                        value={editedImageUrl || b.imageurl || ""}
                        onChange={(e) => setEditedImageUrl(e.target.value)}
                        placeholder="Введите URL изображения"
                        className="formInput"
                    />
                    {editedImageUrl || b.imageurl ? (
                        <img
                        src={
                            (editedImageUrl || b.imageurl).startsWith("http")
                            ? editedImageUrl || b.imageurl
                            : `http://localhost:5000/${editedImageUrl || b.imageurl}`
                        }
                        alt={b.title}
                        className="bookImage"
                        />
                    ) : (
                        <div className="noImage">Нет фото</div>
                    )}
                    </div>
                ) : b.imageurl ? (
                    <img src={b.imageurl} alt={b.title} className="bookImage" />
                ) : (
                    <div className="noImage">Нет фото</div>
                )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <input
                      className="formInput"
                      type="text"
                      value={editedBook.title}
                      onChange={(e) =>
                        handleInputChange("title", e.target.value)
                      }
                      onKeyDown={handleKeyDown}
                      autoFocus
                    />
                  ) : (
                    b.title
                  )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <select
                      className="formInput"
                      value={editedBook.author_id || ""}
                      onChange={(e) =>
                        handleInputChange("author_id", e.target.value)
                      }
                    >
                      <option value="">Выберите автора</option>
                      {authors.map((a) => (
                        <option key={a.id_author} value={a.id_author}>
                          {a.firstname} {a.lastname}
                        </option>
                      ))}
                    </select>
                  ) : (
                    `${b.author_firstname} ${b.author_lastname}`
                  )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <select
                      className="formInput"
                      value={editedBook.category_id || ""}
                      onChange={(e) =>
                        handleInputChange("category_id", e.target.value)
                      }
                    >
                      <option value="">Выберите категорию</option>
                      {categories.map((c) => (
                        <option key={c.id_category} value={c.id_category}>
                          {c.name}
                        </option>
                      ))}
                    </select>
                  ) : (
                    b.category_name
                  )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <input
                      className="formInput"
                      type="number"
                      value={editedBook.price}
                      onChange={(e) =>
                        handleInputChange("price", e.target.value)
                      }
                      onKeyDown={handleKeyDown}
                    />
                  ) : (
                    `${b.price} ₽`
                  )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <input
                      className="formInput"
                      type="number"
                      value={editedBook.quantity}
                      onChange={(e) =>
                        handleInputChange("quantity", e.target.value)
                      }
                      onKeyDown={handleKeyDown}
                    />
                  ) : (
                    b.quantity
                  )}
                </td>

                <td>
                  {editingBookId === b.id_book ? (
                    <button className="menuItem" onClick={handleSaveChanges}>Сохранить
                    </button>
                  ) : (
                    <button
                      className="menuItem"
                      onClick={() => handleEditClick(b)}
                    >Изменить
                    </button>
                  )}
                  <button
                    className="menuItemDanger"
                    onClick={() => handleDelete(b.id_book)}
                  >Удалить
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <Footer />
    </div>
  );
};

export default Products;
