import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header.js';
import Form from '../components/Form.js';

const Login = () => {
  const navigate = useNavigate();
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const { data } = await axios.post('http://localhost:5000/api/accounts/login', { login, password });
      localStorage.setItem('user', JSON.stringify(data));
      navigate('/');
    } catch (err) {
      setError(err?.response?.data?.error || '–ù–µ —É–¥–∞–ª–æ—Å—å –≤–æ–π—Ç–∏.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Header title={'–í—Ö–æ–¥'} description={'–í–≤–µ–¥–∏—Ç–µ –ª–æ–≥–∏–Ω –∏ –ø–∞—Ä–æ–ª—å'} />
      <div className="formContainer">
        <Form
          onSubmit={handleSubmit}
          loading={loading}
          error={error}
          submitLabel={'–í–æ–π—Ç–∏'}
          loadingLabel={'–í—Ö–æ–¥–∏–º...'}
        >
          <label className='formLabel'>–õ–æ–≥–∏–Ω</label>
          <input
            className='formInput'
            type="text"
            value={login}
            onChange={(e) => setLogin(e.target.value)}
            required
          />
          <label className="formLabel">–ü–∞—Ä–æ–ª—å</label>
          <div className='formInput'>
            <input 
              type={showPassword ? 'text' : 'password'} 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button class="btn btn-outline-secondary" type="button" id="button-addon2">{showPassword ? 'üôà' : 'üëÅÔ∏è'}</button>
          </div>
        </Form>
        <div className="hint">
            –ù–µ—Ç –∞–∫–∫–∞—É–Ω—Ç–∞? <Link className='redirectLink' to="/register">–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
