import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Header from "../components/Header.js";
import Footer from "../components/Footer.js";
import api from "../axiosSetup.js";

const Products = () => {
  const [books, setBooks] = useState([]);
  const [editMode, setEditMode] = useState(null); // id –∫–Ω–∏–≥–∏, –∫–æ—Ç–æ—Ä–∞—è —Å–µ–π—á–∞—Å —Ä–µ–¥–∞–∫—Ç–∏—Ä—É–µ—Ç—Å—è
  const [editedBook, setEditedBook] = useState({});

  useEffect(() => {
    api.get("/books").then((res) => setBooks(res.data));
  }, []);

  const handleEditClick = (book) => {
    setEditMode(book.id_book);
    setEditedBook(book);
  };

  const handleCancel = () => {
    setEditMode(null);
    setEditedBook({});
  };

  const handleSave = async (id) => {
    try {
      await api.patch(`/books/${id}`, editedBook);
      setBooks((prev) =>
        prev.map((b) => (b.id_book === id ? editedBook : b))
      );
      setEditMode(null);
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏:", err);
    }
  };

  const handleChange = (field, value) => {
    setEditedBook((prev) => ({ ...prev, [field]: value }));
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/books/${id}`);
      setBooks((prev) => prev.filter((b) => b.id_book !== id));
    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏:", err);
    }
  };

  return (
    <div className="p-6">
      <Header />
      <h1 className="text-2xl font-bold mb-4">–ú–µ–Ω–µ–¥–∂–µ—Ä –∫–Ω–∏–≥</h1>

      <div className="grid gap-4">
        {books.map((b) => (
          <motion.div
            key={b.id_book}
            className="border rounded-xl p-4 shadow-sm"
            whileHover={{ scale: 1.01 }}
          >
            {editMode === b.id_book ? (
              <>
                <input
                  value={editedBook.title || ""}
                  onChange={(e) => handleChange("title", e.target.value)}
                  placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ"
                />
                <input
                  value={editedBook.author || ""}
                  onChange={(e) => handleChange("author", e.target.value)}
                  placeholder="–ê–≤—Ç–æ—Ä"
                  className="mt-2"
                />
                <input
                  value={editedBook.price || ""}
                  onChange={(e) => handleChange("price", e.target.value)}
                  placeholder="–¶–µ–Ω–∞"
                  className="mt-2"
                />

                <div>
                  <button onClick={() => handleSave(b.id_book)}>üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
                  <button variant="secondary" onClick={handleCancel}>
                    ‚ùå –û—Ç–º–µ–Ω–∞
                  </button>
                </div>
              </>
            ) : (
              <>
                <p className="font-semibold">{b.title}</p>
                <p className="text-sm text-gray-600">{b.author}</p>
                <p className="text-sm text-gray-500">{b.price} ‚ÇΩ</p>

                <div className="flex gap-2 mt-3">
                  <Button onClick={() => handleEditClick(b)}>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å</Button>
                  <Button variant="destructive" onClick={() => handleDelete(b.id_book)}>
                    üóë –£–¥–∞–ª–∏—Ç—å
                  </Button>
                </div>
              </>
            )}
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default Products;
