import React, { useEffect, useState } from "react";
import api from "../axiosSetup.js";
import Header from "../components/Header.js";
import { Line, Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Reports = () => {
  const [summary, setSummary] = useState({});
  const [sales, setSales] = useState([]);
  const [categories, setCategories] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    Promise.all([
      api.get("/reports/orders-view"),
      api.get("/reports/books-view"),
      api.get("/reports/top-books-view"),
      api.get("/reports/orderdetails-view"),
      api.get("/reports/usersaccounts-view"),
    ])
      .then(([ordersRes, booksRes, topBooksRes, orderDetailsRes, usersRes]) => {
        const orders = ordersRes.data || [];
        const orderDetails = orderDetailsRes.data || [];
        const topBooks = topBooksRes.data || [];
        const usersData = usersRes.data || [];

        // --- üßÆ –û–±—â–∞—è —Å–≤–æ–¥–∫–∞ ---
        const totalOrders = orders.length;
        const totalRevenue = orderDetails.reduce(
          (acc, o) =>
            acc + parseFloat(o["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"] || 0) * (o["–ö–æ–ª-–≤–æ"] || 0),
          0
        );
        const avgOrderValue =
          totalOrders > 0 ? (totalRevenue / totalOrders).toFixed(2) : 0;

        setSummary({
          total_orders: totalOrders,
          total_revenue: totalRevenue.toFixed(2),
          avg_order_value: avgOrderValue,
        });

        // --- üìà –ü—Ä–æ–¥–∞–∂–∏ –ø–æ –º–µ—Å—è—Ü–∞–º ---
        const salesMap = {};
        orders.forEach((o) => {
          const month = o["–î–∞—Ç–∞ –∑–∞–∫–∞–∑–∞"]?.slice(0, 7) || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ";
          const details = orderDetails.filter(
            (od) => od["–ö–æ–¥ –∑–∞–∫–∞–∑–∞"] === o["–ö–æ–¥ –∑–∞–∫–∞–∑–∞"]
          );
          const revenue = details.reduce(
            (sum, d) =>
              sum + parseFloat(d["–¶–µ–Ω–∞ –∑–∞ –µ–¥–∏–Ω–∏—Ü—É"] || 0) * (d["–ö–æ–ª-–≤–æ"] || 0),
            0
          );
          salesMap[month] = (salesMap[month] || 0) + revenue;
        });

        setSales(
          Object.entries(salesMap).map(([month, revenue]) => ({
            month,
            revenue: revenue.toFixed(2),
          }))
        );

        // --- üìö –¢–æ–ø –∫–∞—Ç–µ–≥–æ—Ä–∏–π (–ø–æ –ø—Ä–æ–¥–∞–∂–∞–º) ---
        const cats = topBooks.map((tb) => ({
          category: tb["–ö–∞—Ç–µ–≥–æ—Ä–∏—è"] || tb["–ñ–∞–Ω—Ä"] || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
          total_sold: tb["–ü—Ä–æ–¥–∞–Ω–æ"] || tb["–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ"] || 0,
        }));
        setCategories(cats);

        // --- üë• –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π ---
        const topUsers = usersData
          .sort(
            (a, b) =>
              parseFloat(b["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0) - parseFloat(a["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0)
          )
          .slice(0, 5)
          .map((u) => ({
            user: u["–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å"] || u["–§–ò–û"] || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–æ",
            total_spent: parseFloat(u["–ü–æ—Ç—Ä–∞—á–µ–Ω–æ"] || 0),
          }));
        setUsers(topUsers);
      })
      .catch((err) => console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω–∏–π:", err));
  }, []);

  // --- üì§ –≠–∫—Å–ø–æ—Ä—Ç CSV ---
  const exportCSV = (data, filename) => {
    if (!Array.isArray(data) || data.length === 0) {
      console.warn("–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞");
      return;
    }

    const csv = [
      Object.keys(data[0]).join(","),
      ...data.map((row) =>
        Object.values(row)
          .map((v) => `"${v}"`)
          .join(",")
      ),
    ].join("\n");

    const blob = new Blob([csv], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
  };

  return (
    <div className="p-6">
      <Header
        title={"–û—Ç—á–µ—Ç—ã –∏ –∞–Ω–∞–ª–∏—Ç–∏–∫–∞"}
        description={
          "–ì—Ä–∞—Ñ–∏–∫–∏ –ø—Ä–æ–¥–∞–∂, —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º –∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è–º. –ú–æ–∂–Ω–æ —ç–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –¥–∞–Ω–Ω—ã–µ –≤ CSV."
        }
      />

      {/* --- –ö–∞—Ä—Ç–æ—á–∫–∏ —Å–≤–æ–¥–∫–∏ --- */}
      <div className="grid grid-cols-3 gap-6 my-6">
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–í—Å–µ–≥–æ –∑–∞–∫–∞–∑–æ–≤</p>
          <h2 className="text-2xl font-semibold">{summary.total_orders}</h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–û–±—â–∞—è –≤—ã—Ä—É—á–∫–∞</p>
          <h2 className="text-2xl font-semibold">{summary.total_revenue} ‚ÇΩ</h2>
        </div>
        <div className="bg-white p-4 rounded-xl shadow">
          <p className="text-gray-500">–°—Ä–µ–¥–Ω–∏–π —á–µ–∫</p>
          <h2 className="text-2xl font-semibold">{summary.avg_order_value} ‚ÇΩ</h2>
        </div>
      </div>

      {/* --- –ì—Ä–∞—Ñ–∏–∫–∏ --- */}
      <div className="grid grid-cols-2 gap-6">
        {/* –ü—Ä–æ–¥–∞–∂–∏ –ø–æ –º–µ—Å—è—Ü–∞–º */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">–ü—Ä–æ–¥–∞–∂–∏ –ø–æ –º–µ—Å—è—Ü–∞–º</h3>
          <Line
            data={{
              labels: sales.map((s) => s.month),
              datasets: [
                {
                  label: "–í—ã—Ä—É—á–∫–∞ (‚ÇΩ)",
                  data: sales.map((s) => s.revenue),
                  borderColor: "rgba(75, 192, 192, 1)",
                  tension: 0.2,
                },
              ],
            }}
          />
        </div>

        {/* –¢–æ–ø –∫–∞—Ç–µ–≥–æ—Ä–∏–π */}
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="text-lg font-semibold mb-2">–¢–æ–ø –∫–∞—Ç–µ–≥–æ—Ä–∏–π</h3>
          <Pie
            data={{
              labels: categories.map((c) => c.category),
              datasets: [
                {
                  data: categories.map((c) => c.total_sold),
                  backgroundColor: [
                    "#36A2EB",
                    "#FF6384",
                    "#FFCE56",
                    "#4BC0C0",
                    "#9966FF",
                  ],
                },
              ],
            }}
          />
        </div>

        {/* –¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π */}
        <div className="bg-white p-4 rounded-xl shadow col-span-2">
          <h3 className="text-lg font-semibold mb-2">–¢–æ–ø –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π</h3>
          <Bar
            data={{
              labels: users.map((u) => u.user),
              datasets: [
                {
                  label: "–°—É–º–º–∞ –ø–æ–∫—É–ø–æ–∫ (‚ÇΩ)",
                  data: users.map((u) => u.total_spent),
                  backgroundColor: "#36A2EB",
                },
              ],
            }}
          />
        </div>
      </div>

      {/* --- –ö–Ω–æ–ø–∫–∏ —ç–∫—Å–ø–æ—Ä—Ç–∞ --- */}
      <div className="mt-6 flex gap-4">
        <button
          disabled={!sales.length}
          className="formButton bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 disabled:bg-gray-400"
          onClick={() => exportCSV(sales, "sales_report.csv")}
        >
          –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ–¥–∞–∂–∏ (CSV)
        </button>

        <button
          disabled={!users.length}
          className="formButton bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 disabled:bg-gray-400"
          onClick={() => exportCSV(users, "top_users.csv")}
        >
          –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π (CSV)
        </button>
      </div>
    </div>
  );
};

export default Reports;
